SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE mprev_cancelar_agenda_turma ( nr_sequencia_p bigint, nm_usuario_p text, dt_referencia_p timestamp, qt_cancelamentos_p INOUT bigint) AS $body$
BEGIN

if ((nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '')) then

	select	count(*)
	into STRICT	qt_cancelamentos_p
	from	mprev_agendamento
	where	dt_agenda > dt_referencia_p
	and	nr_seq_horario_turma = nr_sequencia_p;

	update	mprev_agendamento
	set	ie_status_agenda = 'C',
		dt_cancelamento = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		ds_observacao = wheb_mensagem_pck.get_texto(802575)||'.'
	where	dt_agenda > dt_referencia_p
	and	nr_seq_horario_turma = nr_sequencia_p;

	update	mprev_grupo_col_turma_hor
	set	dt_cancelamento = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
