CREATE OR REPLACE PROCEDURE mprev_cancelar_ativ_part ( nr_seq_participante_p mprev_participante.nr_sequencia%type, ie_canc_agend_ind_p text, ie_canc_plano_atend_fut_p text, ie_excl_part_grupo_p text, ie_excl_part_prog_p text, ie_excl_part_camp_p text, ie_inat_partic_p text, ie_rejeita_part_cap_p text, nr_seq_motivo_canc_ativ_p mprev_motivo_cancel_ativ.nr_sequencia%type, nm_usuario_p text, NR_SEQ_MPREV_CAPTACAO bigint DEFAULT NULL) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Cancela as atividades do participante conforme seleção .
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: HDM - Controle de Participantes
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_canc_ativ_w	mprev_partic_canc_ativ.nr_sequencia%type;


BEGIN

if (nr_seq_participante_p IS NOT NULL AND nr_seq_participante_p::text <> '') then

	select	nextval('mprev_partic_canc_ativ_seq')
	into STRICT	nr_seq_canc_ativ_w
	;

	insert	into mprev_partic_canc_ativ(nr_sequencia,
		dt_atualizacao,
		dt_cancelamento,
		ie_agenda,
		ie_campanhas,
		ie_plano_atend,
		ie_programas,
		ie_situacao_part,
		ie_turmas,
		nm_usuario,
		nr_seq_participante,
		nr_seq_motivo_canc)
	values (nr_seq_canc_ativ_w,
		clock_timestamp(),
		clock_timestamp(),
		ie_canc_agend_ind_p,
		ie_excl_part_camp_p,
		ie_canc_plano_atend_fut_p,
		ie_excl_part_prog_p,
		ie_inat_partic_p,
		ie_excl_part_grupo_p,
		nm_usuario_p,
		nr_seq_participante_p,
		nr_seq_motivo_canc_ativ_p);

	if (coalesce(ie_canc_agend_ind_p,'N') = 'S') then
		CALL mprev_canc_agenda_partic_fut(nr_seq_participante_p, nr_seq_canc_ativ_w, 'N',nm_usuario_p);
	end if;

	if (coalesce(ie_canc_plano_atend_fut_p,'N') = 'S') then
		CALL mprev_canc_plano_partic_fut(nr_seq_participante_p, nr_seq_canc_ativ_w, 'N',nm_usuario_p);
	end if;

	if (coalesce(ie_excl_part_grupo_p,'N') = 'S') then
		CALL mprev_excluir_part_grupos(nr_seq_participante_p, nr_seq_canc_ativ_w, 'N',nm_usuario_p);
	end if;

	if (coalesce(ie_excl_part_prog_p,'N') = 'S') then
		CALL mprev_excluir_part_programas(nr_seq_participante_p, nr_seq_canc_ativ_w, 'N',nm_usuario_p);
	end if;

	if (coalesce(ie_excl_part_camp_p,'N') = 'S') then
		CALL mprev_excluir_part_campanhas(nr_seq_participante_p, nr_seq_canc_ativ_w, 'N',nm_usuario_p);
	end if;

	if (coalesce(ie_inat_partic_p,'N') = 'S') then
		CALL mprev_alterar_sit_partic(nr_seq_participante_p, 'I', 'N',nm_usuario_p);
		CALL mprev_alterar_status_partic(nr_seq_participante_p, '5', 'N', nm_usuario_p);
	end if;

	if (coalesce(ie_rejeita_part_cap_p,'N') = 'S') then
		CALL mprev_alterar_sit_capta(nr_seq_participante_p, 'R', 'N',nm_usuario_p, NR_SEQ_MPREV_CAPTACAO);
	end if;

	commit;

end if;

end;
$body$
