SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE mprev_cancelar_captacoes_adic (nr_seq_captacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;

c_captacao CURSOR FOR
	SELECT	a.nr_sequencia
	from	mprev_captacao a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w
	and	a.ie_status in ('P','T')
	and	nr_sequencia <> nr_seq_captacao_p;

BEGIN

if (nr_seq_captacao_p IS NOT NULL AND nr_seq_captacao_p::text <> '') then
	select	a.cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	mprev_captacao a
	where	a.nr_sequencia	= nr_seq_captacao_p;

	for r_c_captacao in c_captacao loop
		update	mprev_captacao
		set	ie_status	= 'C',
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp(),
			nr_seq_captacao_canc = nr_seq_captacao_p
		where	nr_sequencia	= r_c_captacao.nr_sequencia;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
