CREATE OR REPLACE PROCEDURE mprev_canc_plano_partic_fut ( nr_seq_participante_p bigint, nr_seq_canc_ativ_p mprev_partic_canc_ativ.nr_sequencia%type, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Cancelar plano de atendimento futuro
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: HDM - Controle de Participantes
[  ]  Objetos do dicionario [ x ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_partic_ciclo_item_w	mprev_partic_ciclo_item.nr_sequencia%type;
ie_status_w			mprev_partic_ciclo_item.ie_status%type;
nr_seq_motivo_desvio_w		mprev_motivo_desvio_plano.nr_sequencia%type;
	
c01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_status,
		nr_seq_motivo_desvio
	from  	mprev_partic_ciclo_item_v
	where 	nr_seq_participante = nr_seq_participante_p
	and   	ie_status in ('P','A')
	and   	dt_prevista >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp());
                              	

BEGIN

if (nr_seq_participante_p IS NOT NULL AND nr_seq_participante_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		nr_seq_partic_ciclo_item_w,
		ie_status_w,
		nr_seq_motivo_desvio_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin	
		CALL mprev_alterar_status_plano(nr_seq_partic_ciclo_item_w,
						'C',
						nr_seq_motivo_desvio_w,
						obter_texto_tasy(333300,null),
						nr_seq_canc_ativ_p,
						ie_commit_p,
						'',
						nm_usuario_p);
		end;
		if (coalesce(ie_commit_p, 'S') = 'S') then
			commit;
		end if;
	end loop;					
	close c01;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
