SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE mprev_confirmar_demanda_espont (nr_seq_demanda_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Confirmar a demanda espontânea da Medicina Preventiva.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_captacao_w	mprev_captacao.nr_sequencia%type;
nr_seq_captacao_dest_w	mprev_captacao_destino.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	mprev_captacao_destino
	where	nr_seq_captacao = nr_seq_captacao_w;


BEGIN

if (nr_seq_demanda_p IS NOT NULL AND nr_seq_demanda_p::text <> '') then
	update	mprev_demanda_espont
	set	dt_confirmacao = clock_timestamp(),
		nm_usuario_confirmacao = nm_usuario_p
	where	nr_sequencia = nr_seq_demanda_p;

	update	mprev_captacao
	set	dt_inclusao = clock_timestamp(),
		ie_status = 'P'
	where	nr_seq_demanda_espont = nr_seq_demanda_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_captacao_w
	from	mprev_captacao
	where	NR_SEQ_DEMANDA_ESPONT = nr_seq_demanda_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_captacao_dest_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			CALL mprev_gerar_resp_dest_capt(nr_seq_captacao_dest_w, nm_usuario_p);
		end;
	end loop;
	close C01;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE mprev_confirmar_demanda_espont (nr_seq_demanda_p bigint, nm_usuario_p text) FROM PUBLIC;

