CREATE OR REPLACE PROCEDURE mprev_consistir_repasse_atend ( nr_seq_part_ciclo_item_p bigint, cd_profissional_dest_p text, nr_seq_funcao_dest_p bigint, ds_mensagem_retorno_p INOUT text) AS $body$
DECLARE


ds_mensagem_retorno_w	varchar(255);
qt_registros_w		bigint;


BEGIN

/*Se  o profissional já for o responsável pelo ciclo não insere novamente*/

if (nr_seq_part_ciclo_item_p IS NOT NULL AND nr_seq_part_ciclo_item_p::text <> '') then
	if	((cd_profissional_dest_p IS NOT NULL AND cd_profissional_dest_p::text <> '') or (nr_seq_funcao_dest_p IS NOT NULL AND nr_seq_funcao_dest_p::text <> '')) then

		select	count(1)
		into STRICT	qt_registros_w
		from	mprev_part_cic_item_resp a
		where	a.nr_seq_partic_ciclo_item = nr_seq_part_ciclo_item_p
		and (a.cd_profissional = cd_profissional_dest_p or nr_seq_funcao_colab = nr_seq_funcao_dest_p)
		and	coalesce(a.dt_finalizacao::text, '') = '';

		if (qt_registros_w > 0) then
			--Atenção, este profissional ou função profissional já está como responsável por este atendimento. Deseja apenas finalizar sua responsabilidade?
			ds_mensagem_retorno_w := wheb_mensagem_pck.get_texto(338403);
		end if;
	end if;

ds_mensagem_retorno_p := ds_mensagem_retorno_w;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
