CREATE OR REPLACE PROCEDURE mprev_contrato_grupo_turma ( nr_seq_grupo_turma_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:		Gravar o campo NR_SEQ_CONTRATO na tabela MPREV_GRUPO_COL_TURMA
				pasta (Turma)
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_contrato_w			pls_contrato.nr_contrato%type;
nr_seq_contrato_w			pls_contrato.nr_sequencia%type;

BEGIN

select	nr_contrato
into STRICT	nr_contrato_w
from	mprev_grupo_col_turma
where	nr_sequencia = nr_seq_grupo_turma_p;

select	max(nr_sequencia)
into STRICT	nr_seq_contrato_w
from	pls_contrato
where	nr_contrato	= nr_contrato_w;

if (coalesce(nr_seq_contrato_w,0) <> 0) then
	update	mprev_grupo_col_turma
	set	nr_seq_contrato	= nr_seq_contrato_w
	where	nr_sequencia	= nr_seq_grupo_turma_p;
else
	update	mprev_grupo_col_turma
	set	nr_seq_contrato	 = NULL
	where	nr_sequencia	= nr_seq_grupo_turma_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
