CREATE OR REPLACE PROCEDURE mprev_excluir_part_grupos ( nr_seq_participante_p bigint, nr_seq_canc_ativ_p mprev_partic_canc_ativ.nr_sequencia%type, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Excluir participação em grupos
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: HDM - Controle de Participantes
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_grupo_turma_partic_w	mprev_grupo_turma_partic.nr_sequencia%type;
dt_saida_w			mprev_grupo_turma_partic.dt_saida%type;

c01 CURSOR FOR
	SELECT  nr_sequencia
	from    mprev_grupo_turma_partic
	where   nr_seq_participante = nr_seq_participante_p
	and (coalesce(dt_saida::text, '') = '' or dt_saida > clock_timestamp());

BEGIN

if (nr_seq_participante_p IS NOT NULL AND nr_seq_participante_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		nr_seq_grupo_turma_partic_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		update	mprev_grupo_turma_partic
		set	dt_saida = clock_timestamp(),
			ds_motivo_saida = CASE WHEN ds_motivo_saida='' THEN ''  ELSE ds_motivo_saida||chr(10) END ||obter_texto_tasy(333300,null),
			nr_seq_part_canc_ativ = nr_seq_canc_ativ_p
		where	nr_sequencia = nr_seq_grupo_turma_partic_w;
		end;
	end loop;
	close c01;

	update 	mprev_grupo_turma_espera
	set 	ie_motivo_remocao = 'D',
		ds_motivo_remocao = wheb_mensagem_pck.get_texto(358189),
		dt_remocao = clock_timestamp()
	where 	nr_seq_participante = nr_seq_participante_p
	and 	coalesce(dt_remocao::text, '') = '';


	if (coalesce(ie_commit_p,'S') = 'S') then
		commit;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
