CREATE OR REPLACE PROCEDURE mprev_excl_part_camp_auto ( nr_seq_campanha_p bigint, dt_termino_p timestamp, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Ao informar data de término na campanha, preencher automaticamente a data de exclusão
dos participantes desta campanha
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: HDM - Controle de Participantes
[  ]  Objetos do dicionário [  x] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (nr_seq_campanha_p IS NOT NULL AND nr_seq_campanha_p::text <> '') then

	update	mprev_campanha_partic
	set	dt_exclusao = dt_termino_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_seq_campanha = nr_seq_campanha_p
	and	coalesce(dt_exclusao::text, '') = '';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
