SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE mprev_gerar_comunic_rescisao ( nr_seq_participante_p bigint, dt_rescisao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar uma comunicação interna, avisando os responsáveis pelo paciente quando o mesmo foi rescindido
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
dt_referencia_w		timestamp;
ds_lista_usuario_w	varchar(4000)	:= null;
ds_comunicado_w		varchar(4000);
nm_usuario_w		usuario.nm_usuario%type;
nm_participante_w	pessoa_fisica.nm_pessoa_fisica%type;

/* Obter os profissionais responsáveis do participante */

c01 CURSOR FOR
	/* Responsáveis */

	SELECT	c.nm_usuario
	from	usuario c,
		mprev_programa_partic b,
		mprev_prog_partic_prof a
	where	a.nr_seq_programa_partic = b.nr_sequencia
	and	a.cd_profissional = c.cd_pessoa_fisica
	and	b.nr_seq_participante = nr_seq_participante_p
	and	dt_referencia_w between a.dt_inicio_acomp and coalesce(a.dt_fim_acomp,dt_referencia_w)
	
union

	/* Responsáveis pelas equipes */

	SELECT	b.nm_usuario
	from	usuario b,
		mprev_equipe a
	where	a.cd_pessoa_responsavel = b.cd_pessoa_fisica
	and	a.ie_situacao = 'A';

BEGIN
dt_referencia_w	:= coalesce(dt_rescisao_p,clock_timestamp());

if (nr_seq_participante_p IS NOT NULL AND nr_seq_participante_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nm_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(ds_lista_usuario_w::text, '') = '') then
			ds_lista_usuario_w	:= nm_usuario_w || ',';
		else
			ds_lista_usuario_w	:= ds_lista_usuario_w || ' ' || nm_usuario_w || ',';
		end if;
		end;
	end loop;
	close C01;

	select	obter_nome_pf(a.cd_pessoa_fisica)
	into STRICT	nm_participante_w
	from	mprev_participante a
	where	a.nr_sequencia	= nr_seq_participante_p;

	ds_comunicado_w	:= 	'Mensagem automática: ' || chr(13) || chr(10) ||
				'O participante ' || nm_participante_w || ' foi rescindido de seu contrato em ' || dt_referencia_w || '.';

	CALL gerar_comunic_padrao(clock_timestamp(),
				'Participante da preventiva rescindido',
				ds_comunicado_w,
				nm_usuario_p,
				'N',
				ds_lista_usuario_w,
				'N',
				null,null,null,null,clock_timestamp(),null,null);
end if;

/* Não pode ter commit */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE mprev_gerar_comunic_rescisao ( nr_seq_participante_p bigint, dt_rescisao_p timestamp, nm_usuario_p text) FROM PUBLIC;

