CREATE OR REPLACE PROCEDURE mprev_gerar_texto_relatorio ( nr_seq_participante_p bigint, cd_classif_relat_p RELATORIO.CD_CLASSIF_RELAT%type, CD_RELATORIO_p RELATORIO.CD_RELATORIO%type, nm_usuario_p text) AS $body$
DECLARE

			 
nr_seq_mprev_txt_rel_cont_w	bigint;
ds_conteudo_w			text;
ds_conteudo_ww			text;
nr_seq_relatorio_w		bigint;
			
c01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	mprev_texto_relat		b, 
		mprev_texto_relat_conteudo	a 
	where	a.nr_seq_texto_relat		= b.nr_sequencia 
	and	b.nr_seq_relatorio		= nr_seq_relatorio_w 
	and	clock_timestamp()	between b.dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp()) 
	order by coalesce(a.nr_seq_ordem,0);
			

BEGIN 
 
delete	FROM W_MPREV_RELAT_TEXTO 
where	nm_usuario	= nm_usuario_p;
 
select	max(nr_sequencia) 
into STRICT	nr_seq_relatorio_w 
from	relatorio 
where	cd_classif_relat	= cd_classif_relat_p 
and	CD_RELATORIO		= CD_RELATORIO_p;
 
open c01;
loop 
fetch c01 into	 
	nr_seq_mprev_txt_rel_cont_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	ds_conteudo_w := MPREV_relatorio_pck.imprimir_relat_mprev_data(nr_seq_participante_p, nr_seq_mprev_txt_rel_cont_w, ds_conteudo_w);
	 
	select	substr(ds_conteudo_w,1,32764) 
	into STRICT	ds_conteudo_ww 
	;
	 
	insert into W_MPREV_RELAT_TEXTO(	nr_sequencia,dt_atualizacao,nm_usuario,DS_TEXTO) 
	values (	nextval('w_mprev_relat_texto_seq'),clock_timestamp(),nm_usuario_p,ds_conteudo_ww);
	 
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
