SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE mprev_inserir_partic_turma ( nr_seq_participante_p bigint, nr_seq_turma_p bigint, dt_entrada_p timestamp, nm_usuario_p text, ie_incluso_p INOUT text, nm_incluso_grupo_p INOUT text, nr_seq_grupo_esp_p bigint default null) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Inserir as pessoas da captação nos grupos selecionados
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nm_incluso_grupo_w	varchar(255);
ie_incluso_grupo_w		varchar(1);
qt_existente_w		integer;
ds_limite_maximo_w		varchar(255);
ds_limite_maximo_aux_w	varchar(255);
ie_motivo_w		varchar(1);
ds_motivo_w		varchar(255);
nr_seq_grupo_w		mprev_grupo_coletivo.nr_sequencia%type;
ds_observacao_w		mprev_grupo_turma_espera.ds_observacao%type;

C01 CURSOR(	nr_seq_participante_pc	mprev_participante.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from 	mprev_grupo_turma_espera a
	where 	a.nr_seq_participante = nr_seq_participante_p
	and	a.nr_seq_turma	= nr_seq_turma_p
	and 	coalesce(a.dt_remocao::text, '') = ''
	
union all

	SELECT	a.nr_sequencia
	from 	mprev_grupo_turma_espera a
	where 	a.nr_seq_participante = nr_seq_participante_p
	and	a.nr_seq_grupo	= nr_seq_grupo_w
	and 	coalesce(a.dt_remocao::text, '') = '';

BEGIN

select	a.nr_sequencia,
	substr(a.nm_grupo || ' - ' || b.nm_turma || ' - ' ||
	(select	count(1)
	from	mprev_grupo_turma_partic x
	where	x.nr_seq_turma = b.nr_sequencia
	and	clock_timestamp() between x.dt_entrada and coalesce(x.dt_saida,clock_timestamp())) || ' de ' ||
	b.qt_max_participante,1,255)
into STRICT	nr_seq_grupo_w,
	nm_incluso_grupo_w
from	mprev_grupo_col_turma b,
	mprev_grupo_coletivo a
where	a.nr_sequencia	= b.nr_seq_grupo_coletivo
and	b.nr_sequencia	= nr_seq_turma_p;

select	count(1)
into STRICT	qt_existente_w
from	mprev_grupo_turma_partic a
where	nr_seq_participante = nr_seq_participante_p
and		nr_seq_turma = nr_seq_turma_p
and		clock_timestamp() between a.dt_entrada and coalesce(a.dt_saida,clock_timestamp());

SELECT * FROM mprev_consiste_inc_part_turma(nr_seq_turma_p, null, dt_entrada_p, nr_seq_participante_p, nm_usuario_p, ie_motivo_w, ds_motivo_w) INTO STRICT ie_motivo_w, ds_motivo_w;

if (qt_existente_w = 0) and (coalesce(ds_motivo_w::text, '') = '') then

 	select	max(ds_observacao)
	into STRICT	ds_observacao_w
	from 	mprev_grupo_turma_espera
	where 	nr_sequencia = nr_seq_grupo_esp_p;

	insert into mprev_grupo_turma_partic(nr_sequencia,
			 dt_atualizacao,
			 nm_usuario,
			 dt_atualizacao_nrec,
			 nm_usuario_nrec,
			 nr_seq_turma,
			 nr_seq_participante,
			 dt_entrada,
			 dt_saida,
			 ds_observacao)
		values (    nextval('mprev_grupo_turma_partic_seq'),
			 clock_timestamp(),
			 nm_usuario_p,
			 clock_timestamp(),
			 nm_usuario_p,
			 nr_seq_turma_p,
			 nr_seq_participante_p,
			 dt_entrada_p,
			 null,
			 ds_observacao_w);
	ie_incluso_grupo_w	:= 'S';

	for	rw_c01_w in C01(nr_seq_participante_p) loop
		CALL mprev_remover_lista_espera(	rw_c01_w.nr_sequencia,
						clock_timestamp(),
						'I',
						'',
						nm_usuario_p);
	end loop;

else
	if (ds_motivo_w IS NOT NULL AND ds_motivo_w::text <> '') then
		ds_limite_maximo_aux_w	:= ' ( '|| ds_motivo_w || ')';
	else
		ds_limite_maximo_aux_w	:= ' (' || wheb_mensagem_pck.get_texto(802576) || ')';
	end if;
	ie_incluso_grupo_w	:= 'N';
end if;

nm_incluso_grupo_p	:= nm_incluso_grupo_w || ds_limite_maximo_aux_w;
ie_incluso_p		:= ie_incluso_grupo_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE mprev_inserir_partic_turma ( nr_seq_participante_p bigint, nr_seq_turma_p bigint, dt_entrada_p timestamp, nm_usuario_p text, ie_incluso_p INOUT text, nm_incluso_grupo_p INOUT text, nr_seq_grupo_esp_p bigint default null) FROM PUBLIC;

