CREATE OR REPLACE PROCEDURE mprev_marcacao_horario_transf ( nr_seq_agendamento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
'L' - LIMPAR MARCAÇÂO DE TRANSFERÊNCIA
'M' - MARCAR PARA TRANSFERÊNCIA
*/
nr_sequencia_w	bigint;


BEGIN
if (ie_opcao_p = 'L') then
	delete	from w_mprev_agendamen_marcacao
	where	nm_usuario = nm_usuario_p;
elsif (ie_opcao_p = 'M') then
	insert into w_mprev_agendamen_marcacao(	nr_sequencia,
						nr_seq_agendamento,
						dt_atualizacao,
						nm_usuario)
					values (	nextval('w_mprev_agendamen_marcacao_seq'),
						nr_seq_agendamento_p,
						clock_timestamp(),
						nm_usuario_p);
end if;

commit;

end;
