CREATE OR REPLACE PROCEDURE mprev_obter_se_part_espera (nr_seq_participante_p bigint, dt_solicitacao_p timestamp, nr_seq_grupo_p bigint, nr_seq_turma_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

if (nr_seq_participante_p IS NOT NULL AND nr_seq_participante_p::text <> '') then

	if (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') then
		select	count(1)
		into STRICT	qt_registros_w
		from	mprev_grupo_turma_espera a
		where	a.nr_seq_participante = nr_seq_participante_p
		and	a.nr_seq_grupo = nr_seq_grupo_p
		and	coalesce(dt_remocao::text, '') = ''
		and	a.nr_sequencia <> nr_sequencia_p;

		if (qt_registros_w > 0) then
			--'Participante j치 incluso na lista de espera.'
			CALL wheb_mensagem_pck.exibir_mensagem_abort(334451);
		end if;

		select  count(1)
		into STRICT	qt_registros_w
		from	mprev_grupo_turma_partic a,
			mprev_grupo_col_turma b
		where	a.nr_seq_turma = b.nr_sequencia
		and	a.nr_seq_participante = nr_seq_participante_p
		and	b.nr_seq_grupo_coletivo = nr_seq_grupo_p
		and (a.dt_saida >= dt_solicitacao_p or coalesce(a.dt_saida::text, '') = '');

		if (qt_registros_w > 0) then
			--'Participante j치 incluso em uma das turmas deste grupo.'
			CALL wheb_mensagem_pck.exibir_mensagem_abort(334452);
		end if;
	elsif (nr_seq_turma_p IS NOT NULL AND nr_seq_turma_p::text <> '') then
		select	count(1)
		into STRICT	qt_registros_w
		from	mprev_grupo_turma_espera a
		where	a.nr_seq_participante = nr_seq_participante_p
		and	a.nr_seq_turma = nr_seq_turma_p
		and	coalesce(dt_remocao::text, '') = ''
		and	nr_sequencia <> nr_sequencia_p;

		if (qt_registros_w > 0) then
			--'Participante j치 incluso na lista de espera.'
			CALL wheb_mensagem_pck.exibir_mensagem_abort(334451);
		end if;

		select  count(1)
		into STRICT	qt_registros_w
		from	mprev_grupo_turma_partic a
		where	a.nr_seq_participante = nr_seq_participante_p
		and	a.nr_seq_turma = nr_seq_turma_p
		and (a.dt_saida >= dt_solicitacao_p or coalesce(a.dt_saida::text, '') = '');

		if (qt_registros_w > 0) then
			--'Participante j치 incluso nesta turma.'
			CALL wheb_mensagem_pck.exibir_mensagem_abort(334453);
		end if;
	end if;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE mprev_obter_se_part_espera (nr_seq_participante_p bigint, dt_solicitacao_p timestamp, nr_seq_grupo_p bigint, nr_seq_turma_p bigint, nr_sequencia_p bigint) FROM PUBLIC;

