CREATE OR REPLACE PROCEDURE mprev_parecer_turma_atend ( nr_seq_aprovacao_p mprev_grupo_turma_aprov.nr_sequencia%type, ie_aprovacao_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_aprovacao_p IS NOT NULL AND nr_seq_aprovacao_p::text <> '') then

	update		mprev_grupo_turma_aprov
	set		dt_aprovacao	= CASE WHEN ie_aprovacao_p='S' THEN clock_timestamp()  ELSE null END ,
			dt_negacao	= CASE WHEN ie_aprovacao_p='N' THEN clock_timestamp()  ELSE null END
	where		nr_sequencia	= nr_seq_aprovacao_p;

end if;

commit;

end;
