CREATE OR REPLACE PROCEDURE mprev_replicar_obs_presente (NR_SEQ_AGENDAMENTO_P bigint, DS_OBSERVACAO_P text, NM_USUARIO_P text) AS $body$
BEGIN
  IF (coalesce(NR_SEQ_AGENDAMENTO_P, 0) > 0) THEN
    UPDATE MPREV_SEL_PARTIC_AGENDA MPA
       SET MPA.DS_JUSTIFICATIVA_FALTA = DS_OBSERVACAO_P,
           MPA.DT_ATUALIZACAO         = clock_timestamp(),
           MPA.NM_USUARIO             = NM_USUARIO_P
     WHERE MPA.NR_SEQ_AGENDAMENTO = NR_SEQ_AGENDAMENTO_P
       AND MPA.IE_SELECIONADO = 'S'
       AND coalesce(trim(both MPA.DS_JUSTIFICATIVA_FALTA)::text, '') = '';

    COMMIT;
  END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
