CREATE OR REPLACE PROCEDURE mprev_selecionar_pf_pop_alvo ( nr_sequencia_p mprev_pop_alvo_pessoa.nr_sequencia%type, ie_selecionada_p mprev_pop_alvo_pessoa.ie_selecionada%type, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualizar se a pessoa está listada para captação ou não.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: MedPrev - Pesquisa de População Alvo.
[  ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/*
S  - Passa a pessoa para a lista de captação.
N - Retira a pessoa da lista de captação.
*/
BEGIN

	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (upper(ie_selecionada_p) = 'S' or (upper(ie_selecionada_p) = 'N')) then

		update	mprev_pop_alvo_pessoa
		set	ie_selecionada	 	= upper(ie_selecionada_p),
			nm_usuario 		= nm_usuario_p,
			dt_atualizacao 		= clock_timestamp()
		where	nr_sequencia		= nr_sequencia_p;

	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
