SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE mprev_tratar_exclusao_part ( nr_seq_segurado_p bigint, cd_pessoa_fisica_p text, nm_pessoa_fisica_p text, ie_origem_p text, dt_rescisao_p timestamp, dt_obito_p timestamp, ie_situacao_partic_p text, cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Tratar exclusão do participante da medicina preventiva quando o mesmo for rescindido ou em óbito
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registros_w			bigint;
nr_seq_participante_w		mprev_participante.nr_sequencia%type;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
ie_benef_plano_saude_w		varchar(1)	:= 'N';
ie_tipo_participante_w		mprev_config_geral.ie_tipo_participante%type;
ie_rescisao_participante_w	mprev_config_geral.ie_rescisao_participante%type;
ie_obito_participante_w		mprev_config_geral.ie_obito_participante%type;
ie_origem_w			varchar(1);


BEGIN

begin
	select	coalesce(a.ie_tipo_participante,'N'),
		coalesce(a.ie_rescisao_participante,'N'),
		coalesce(a.ie_obito_participante,'N')
	into STRICT	ie_tipo_participante_w,
		ie_rescisao_participante_w,
		ie_obito_participante_w
	from	mprev_config_geral a
	where 	cd_estabelecimento = cd_estabelecimento_p;
	exception
		when no_data_found then
		ie_tipo_participante_w		:= 'N';
		ie_rescisao_participante_w 	:= 'N';
		ie_obito_participante_w		:= 'N';
end;

if (ie_tipo_participante_w <> 'N') or (ie_rescisao_participante_w <> 'N') or (ie_obito_participante_w <> 'N') then

	if (coalesce(cd_pessoa_fisica_p,0) > 0) then

		if (coalesce(nr_seq_segurado_p,0) > 0) then
			-- Seta sim para o parametro ie_benef_plano_saude_w já que é um segurado.
			ie_benef_plano_saude_w	:= 'S';
		else
			--Verificar se a pessoa é beneficiária do plano de saúde.
			select	count(1)
			into STRICT	qt_registros_w
			from	pls_segurado a
			where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

			if (qt_registros_w > 0) then
				ie_benef_plano_saude_w	:= 'S';
			end if;
		end if;

		-- Obter a sequencia do participante da medicina preventiva.
		nr_seq_participante_w	:= mprev_obter_participante(cd_pessoa_fisica_p);

	end if;

	--Verifica se a pessoa é participante da medicina preventiva se não for não realiza as ações.
	if (coalesce(nr_seq_participante_w,0) > 0) then

		if (ie_origem_p IS NOT NULL AND ie_origem_p::text <> '') then
			ie_origem_w := ie_origem_p;
		else
			ie_origem_w := 'N';
		end if;

		--Verifica se vai considerar as configurações pelo tipo de participante. Se a configuração é apenas para beneficiários do plano de saúde 'B' ou para todas as pessoas.
		--Se não se encaixar na configuração não vai realizar as ações.
		if	(ie_tipo_participante_w = 'B' AND ie_benef_plano_saude_w = 'S') or (ie_tipo_participante_w = 'Q') then

			--R (rescisão) ou S (óbito)
			if (ie_origem_w = 'R') or (ie_origem_w = 'S') then
				--(C) - Comunicar equipe
				if	((ie_origem_w = 'R') and ((ie_rescisao_participante_w = 'C') or (ie_rescisao_participante_w = 'CSA'))) or
					((ie_origem_w = 'S') and ((ie_obito_participante_w = 'C') or (ie_obito_participante_w = 'CSA'))) then
					--Comunicar rescisão do beneficiário
					CALL mprev_gerar_comunic_exc_part(	nr_seq_participante_w,
									nm_pessoa_fisica_p,
									dt_rescisao_p,
									dt_obito_p,
									ie_origem_w,
									null,
									nm_usuario_p);
				end if;
				--(CSA) - Comunicar equipe, inativar o mesmo e cancelar atividades ou -- (SA) - Inativar o mesmo e cancelar atividades
				if	((ie_origem_w = 'R') and ((ie_rescisao_participante_w = 'CSA') or (ie_rescisao_participante_w = 'SA'))) or
					((ie_origem_w = 'S') and ((ie_obito_participante_w = 'CSA') or (ie_obito_participante_w = 'SA'))) then
					--Cacela agendamentos futuros
					CALL mprev_canc_agenda_partic_fut(nr_seq_participante_w, null, 'N', nm_usuario_p);
					--Cancela plano de atendimento futuro
					CALL mprev_canc_plano_partic_fut(nr_seq_participante_w, null, 'N', nm_usuario_p);
					--Excluir paticipante dos grupos de atendimento
					CALL mprev_excluir_part_grupos(nr_seq_participante_w, null, 'N', nm_usuario_p);
					--Excluir participante dos programas
					CALL mprev_excluir_part_programas(nr_seq_participante_w, null, 'N', nm_usuario_p);
					--Excluir participante de campanhas
					CALL mprev_excluir_part_campanhas(nr_seq_participante_w, null, 'N', nm_usuario_p);
					--Inativar participante
					CALL mprev_alterar_sit_partic(nr_seq_participante_w, ie_situacao_partic_p, 'N',nm_usuario_p);
					--Cancela captação pendente ou em triagem
					CALL mprev_alt_sit_capta(nr_seq_participante_w, nr_seq_segurado_p, 'C', 'N',nm_usuario_p);
					--Altera status do participante para Saída
					CALL mprev_alterar_status_partic(nr_seq_participante_w, '5', 'N', nm_usuario_p);
				end if;
			end if;
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE mprev_tratar_exclusao_part ( nr_seq_segurado_p bigint, cd_pessoa_fisica_p text, nm_pessoa_fisica_p text, ie_origem_p text, dt_rescisao_p timestamp, dt_obito_p timestamp, ie_situacao_partic_p text, cd_estabelecimento_p text, nm_usuario_p text) FROM PUBLIC;

