SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE mp_adicionar_proc_obj_ref ( nr_seq_proc_obj_origem_p bigint, nr_seq_processo_destino_p bigint, nr_seq_proc_obj_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_obj_destino_w	bigint;
nr_seq_objeto_conexao_w		bigint;
qt_altura_w			bigint;
qt_largura_w			bigint;
nr_seq_objeto_fluxo_w		bigint;
ds_objeto_w			varchar(255);


BEGIN

if (nr_seq_proc_obj_origem_p IS NOT NULL AND nr_seq_proc_obj_origem_p::text <> '') and (nr_seq_processo_destino_p IS NOT NULL AND nr_seq_processo_destino_p::text <> '') and (nr_seq_proc_obj_destino_p IS NOT NULL AND nr_seq_proc_obj_destino_p::text <> '') then


	select	max(a.nr_sequencia)
	into STRICT	nr_seq_objeto_conexao_w
	from	mp_grupo_objeto b,
		mp_objeto a
	where	a.nr_seq_grupo	= b.nr_sequencia
	and	b.ie_tipo_grupo	= 'C';

	if (coalesce(nr_seq_objeto_conexao_w::text, '') = '') then
		-- É necessário ter um objeto do tipo "Conexão" cadastrado!
		CALL wheb_mensagem_pck.exibir_mensagem_abort(267092);
	end if;

	select	max(a.nr_sequencia)
	into STRICT	nr_seq_objeto_fluxo_w
	from	mp_grupo_objeto b,
		mp_objeto a
	where	a.nr_seq_grupo	= b.nr_sequencia
	and	b.ie_tipo_grupo	= 'F'
	and	a.ie_estilo_fluxo = 'I';

	if (coalesce(nr_seq_objeto_fluxo_w::text, '') = '') then
		-- É necessário ter um objeto do tipo fluxo com estilo "Lados invertidos" cadastrado!
		CALL wheb_mensagem_pck.exibir_mensagem_abort(267093);
	end if;

	select	qt_largura,
		qt_altura
	into STRICT	qt_largura_w,
		qt_altura_w
	from	mp_objeto
	where	nr_sequencia	= nr_seq_objeto_conexao_w;

	select	to_char(count(*) + 1)
	into STRICT	ds_objeto_w
	from	mp_processo_objeto a
	where	a.nr_seq_processo	= nr_seq_processo_destino_p
	and	mp_obter_tipo_grupo_obj(a.nr_sequencia)	= 'C';

	select	nextval('mp_processo_objeto_seq')
	into STRICT	nr_seq_proc_obj_destino_w
	;

	insert	into	mp_processo_objeto(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_objeto,
		nr_seq_processo,
		qt_topo,
		qt_altura,
		qt_esquerda,
		qt_largura,
		nm_objeto)
	values (nr_seq_proc_obj_destino_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_objeto_conexao_w,
		nr_seq_processo_destino_p,
		10,
		qt_altura_w,
		10,
		qt_largura_w,
		ds_objeto_w);

	/* Criar fluxo com a atividade */

	insert	into	mp_processo_objeto(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_processo,
		nr_seq_objeto,
		nr_seq_obj_origem,
		nr_seq_obj_destino)
	values (nextval('mp_processo_objeto_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_processo_destino_p,
		nr_seq_objeto_fluxo_w,
		nr_seq_proc_obj_destino_w,
		nr_seq_proc_obj_destino_p);


	insert	into	mp_proc_obj_ref(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_proc_obj_origem,
		nr_seq_proc_obj_destino)
	values (nextval('mp_proc_obj_ref_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_proc_obj_origem_p,
		nr_seq_proc_obj_destino_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE mp_adicionar_proc_obj_ref ( nr_seq_proc_obj_origem_p bigint, nr_seq_processo_destino_p bigint, nr_seq_proc_obj_destino_p bigint, nm_usuario_p text) FROM PUBLIC;

