CREATE OR REPLACE PROCEDURE mp_alterar_dono_processo (nr_seq_processo_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_atual_w	varchar(10);


BEGIN

if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_atual_w
	from	mp_dono
	where	nr_seq_proc	= nr_seq_processo_p
	and	coalesce(dt_fim::text, '') = '';

	if (cd_pessoa_atual_w = cd_pessoa_fisica_p) then
		RAISE EXCEPTION '%', 'Esta pessoa já é o dono atual do processo.' USING ERRCODE = '45011';
	end if;

	update	mp_dono
	set	dt_fim	= clock_timestamp()
	where	nr_seq_proc	= nr_seq_processo_p
	and	coalesce(dt_fim::text, '') = '';

	insert	into	mp_dono(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_proc,
		dt_inicio,
		cd_pessoa_fisica)
	values (nextval('mp_dono_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_processo_p,
		clock_timestamp(),
		cd_pessoa_fisica_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
