CREATE OR REPLACE PROCEDURE mp_atualiza_gradiente (nr_seq_objeto_fonte_p bigint, nr_seq_objeto_fundo_p bigint, ie_borda_p text, qt_angulo_p bigint, qt_balanco_p bigint, ie_direcao_p text, ds_cor_inicio_p text, ds_cor_meio_p text, ds_cor_fim_p text, qt_radianos_x_p bigint, qt_radianos_y_p bigint, ie_visivel_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_gradiente_w	bigint;


BEGIN

if (nr_seq_objeto_fonte_p IS NOT NULL AND nr_seq_objeto_fonte_p::text <> '') then

	select	max(a.nr_sequencia)
	into STRICT	nr_seq_gradiente_w
	from	mp_gradiente a
	where	nr_seq_objeto_fonte	= nr_seq_objeto_fonte_p;

	if (coalesce(nr_seq_gradiente_w::text, '') = '') then
		insert	into	mp_gradiente(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			ie_borda,
			qt_angulo,
			qt_balanco,
			ie_direcao,
			ds_cor_inicio,
			ds_cor_meio,
			ds_cor_fim,
			qt_radianos_x,
			qt_radianos_y,
			ie_visivel,
			nr_seq_objeto_fonte)
		values (nextval('mp_gradiente_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			ie_borda_p,
			qt_angulo_p,
			qt_balanco_p,
			ie_direcao_p,
			ds_cor_inicio_p,
			ds_cor_meio_p,
			ds_cor_fim_p,
			qt_radianos_x_p,
			qt_radianos_y_p,
			ie_visivel_p,
			nr_seq_objeto_fonte_p);
	else
		update	mp_gradiente
		set	ie_borda	= ie_borda_p,
			qt_angulo	= qt_angulo_p,
			qt_balanco	= qt_balanco_p,
			ie_direcao	= ie_direcao_p,
			ds_cor_inicio	= ds_cor_inicio_p,
			ds_cor_meio	= ds_cor_meio_p,
			ds_cor_fim	= ds_cor_fim_p,
			qt_radianos_x	= qt_radianos_x_p,
			qt_radianos_y	= qt_radianos_y_p,
			ie_visivel	= ie_visivel_p
		where	nr_sequencia	= nr_seq_objeto_fonte_p;

	end if;
end if;

commit;

end;
$body$
