CREATE OR REPLACE PROCEDURE mp_atualiza_raia ( nr_sequencia_p bigint, qt_topo_p bigint, qt_esquerda_p bigint, qt_altura_p bigint, qt_largura_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_processo_w	bigint;
nr_seq_proc_objeto_w	bigint;

/* Atualizar unidades */

c01 CURSOR FOR
SELECT	nr_sequencia
from	mp_processo_objeto
where	nr_seq_processo	= nr_seq_processo_w;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	nr_seq_processo
	into STRICT	nr_seq_processo_w
	from	mp_processo_raia
	where	nr_sequencia	= nr_sequencia_p;

	update	mp_processo_raia
	set	qt_topo		= qt_topo_p,
		qt_esquerda	= qt_esquerda_p,
		qt_altura	= qt_altura_p,
		qt_largura	= qt_largura_p
	where	nr_sequencia	= nr_sequencia_p;


open c01;
loop
fetch c01 into
	nr_seq_proc_objeto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL mp_atualizar_unid_org(nr_seq_proc_objeto_w,nm_usuario_p);
end loop;
close c01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
