	and	nr_seq_processo	= nr_seq_processo_w;

	delete from	mp_proc_obj_unid_org a
	where	a.nr_seq_unid_org	= nr_seq_unid_org_w
	and	exists (SELECT	1
			from	mp_processo_objeto x
			where	x.nr_sequencia		= a.nr_seq_proc_obj
			and	x.nr_seq_processo 	= nr_seq_processo_w);
end if;

