CREATE OR REPLACE PROCEDURE muda_status_estacionamento ( nr_seq_estacionamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_situacao_w  varchar(2);


BEGIN

select	ie_situacao
into STRICT	ie_situacao_w
from	estacionamento
where	nr_sequencia = nr_seq_estacionamento_p;

if (ie_situacao_w = 'A') then
	ie_situacao_w := 'I';
else
	ie_situacao_w := 'A';

end if;

update	estacionamento
set		ie_situacao = ie_situacao_w
where	nr_sequencia = nr_seq_estacionamento_p;
commit;

end;
$body$
