CREATE OR REPLACE PROCEDURE muda_status_ordem ( nr_seq_ordem_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = 'E') then
	update 	can_ordem_prod
	set	dt_expedido = clock_timestamp(),
		nm_expedido = nm_usuario_p
	where	nr_sequencia = nr_seq_ordem_p;
elsif (ie_opcao_p = 'R') then
	update 	can_ordem_prod
	set	dt_recebido = clock_timestamp(),
		nm_recebido = nm_usuario_p
	where	nr_sequencia = nr_seq_ordem_p;
elsif (ie_opcao_p = 'N') then
	update 	can_ordem_prod
	set	dt_entregue = clock_timestamp(),
		nm_entregue = nm_usuario_p
	where	nr_sequencia = nr_seq_ordem_p;
end if;

commit;

end;
$body$
