CREATE OR REPLACE PROCEDURE mxm_consistir_pf_pj ( cd_pessoa_fisica_p text, cd_cgc_p text, ie_possui_sis_ant_p INOUT text) AS $body$
DECLARE


cd_sistema_ant_pf_w	varchar(20);
cd_sistema_ant_pj_w	varchar(20);


BEGIN

cd_sistema_ant_pf_w	:= null;
cd_sistema_ant_pj_w	:= null;
ie_possui_sis_ant_p	:= 'N';

select	max(cd_sistema_ant)
into STRICT	cd_sistema_ant_pf_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

select	max(cd_sistema_ant)
into STRICT	cd_sistema_ant_pj_w
from	pessoa_juridica
where	cd_cgc = cd_cgc_p;

if (cd_sistema_ant_pf_w IS NOT NULL AND cd_sistema_ant_pf_w::text <> '') or (cd_sistema_ant_pj_w IS NOT NULL AND cd_sistema_ant_pj_w::text <> '') then
	ie_possui_sis_ant_p	:= 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
