SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE mx_cfdi_excluir_arquivos ( nr_sequencia_p bigint) AS $body$
DECLARE


nr_sequencia_w		nf_imp_nota.nr_sequencia%type;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select 	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from 	nf_imp_nota
	where 	coalesce(nr_seq_nf_interno::text, '') = ''
	and 	nr_seq_nf_imp_arquivo in (nr_sequencia_p);

	delete 	from nf_imp_nota_relacao
	where 	nr_seq_nf_imp_nota = nr_sequencia_w;

	delete 	from nf_imp_nota_item_reg_aduan
	where 	nr_seq_nf_imp_nota_item = nr_sequencia_w;

	delete 	from nf_imp_nota_reg_imob
	where 	nr_seq_nf_imp_nota_item = nr_sequencia_w;

	delete 	from nf_imp_nota_imposto
	where 	nr_seq_nf_imp_nota = nr_sequencia_w
	or 	nr_seq_nf_imp_nota_item in (SELECT nr_sequencia from nf_imp_nota_item where nr_seq_nf_imp_nota = nr_sequencia_w);

	delete 	from fis_dados_compl_import
	where 	nr_seq_nf_imp = nr_sequencia_p;

	delete 	from nf_imp_nota_item
	where 	nr_seq_nf_imp_nota = nr_sequencia_w;

	delete 	from nf_imp_nota
	where 	coalesce(nr_seq_nf_interno::text, '') = ''
	and 	nr_seq_nf_imp_arquivo in (nr_sequencia_p);

	delete 	from nf_imp_arquivo
	where 	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
