CREATE OR REPLACE PROCEDURE mx_cfdi_gerar_nf_imp_item ( nr_sequencia_p bigint, nr_nota_fiscal_p text, nr_item_nf_p bigint, nr_sequencia_nf_p bigint, cd_cgc_emitente_p text) AS $body$
DECLARE


vl_tributo_w				bigint;
vl_base_calculo_w			bigint;
tx_tributo_w				bigint;
cd_tributo_w				bigint;
cd_serie_nf_w				nf_imp_nota.ds_serie%type;
nr_item_nf_w				bigint;
qt_existe_w					smallint;


BEGIN

	select	count(*)
	into STRICT	qt_existe_w
	from	nf_imp_nota a,
			nf_imp_nota_item b,
			nf_imp_nota_imposto c
	where	a.nr_seq_nf_interno = nr_sequencia_p
	and		a.nr_sequencia = b.nr_seq_nf_imp_nota
	and		a.nr_sequencia = c.nr_seq_nf_imp_nota
	and		b.nr_sequencia = c.nr_seq_nf_imp_nota_item  LIMIT 1;

	if (qt_existe_w > 0) then

		select	max(c.vl_imposto),
				max(c.vl_base_calculo),
				max(c.tx_imposto),
				max(c.cd_imposto_interno),
				max(a.ds_serie)
		into STRICT	vl_tributo_w,
				vl_base_calculo_w,
				tx_tributo_w,
				cd_tributo_w,
				cd_serie_nf_w
		from	nf_imp_nota a,
				nf_imp_nota_item b,
				nf_imp_nota_imposto c
		where	a.nr_seq_nf_interno = nr_sequencia_p
		and		a.nr_sequencia = b.nr_seq_nf_imp_nota
		and		a.nr_sequencia = c.nr_seq_nf_imp_nota
		and		b.nr_sequencia = c.nr_seq_nf_imp_nota_item;

		insert into nota_fiscal_item_trib(
				nr_sequencia,
				cd_estabelecimento,
				cd_cgc_emitente,
				cd_serie_nf,
				nr_sequencia_nf,
				nr_item_nf,
				cd_tributo,
				vl_tributo,
				dt_atualizacao,
				nm_usuario,
				vl_base_calculo,
				tx_tributo,
				ie_rateio,
				vl_trib_nao_retido,
				vl_base_nao_retido,
				vl_trib_adic,
				vl_base_adic,
				nr_nota_fiscal)
		values (
				nr_sequencia_p,
				wheb_usuario_pck.get_cd_estabelecimento,
				cd_cgc_emitente_p,
				coalesce(cd_serie_nf_w, ''),--cd_serie_nf,
				nr_sequencia_nf_p,
				nr_item_nf_p,
				cd_tributo_w,
				vl_tributo_w,
				clock_timestamp(),
				wheb_usuario_pck.get_nm_usuario,
				vl_base_calculo_w,
				tx_tributo_w,
				'N',--ie_rateio,
				0,
				0,
				0,
				0,
				nr_nota_fiscal_p);-- pode ser null
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
