SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE mx_cfdi_gerar_nf_imp (nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_interno_w			bigint;
vl_tributo_w				bigint;
vl_base_calculo_w			bigint;
tx_tributo_w				bigint;
cd_imposto_interno_w		bigint;
ie_retencao_w				nota_fiscal_trib.ie_retencao%type;
qt_existe_w					smallint;


BEGIN

	select	count(*)
	into STRICT	qt_existe_w
	from	nf_imp_nota a,
			nf_imp_nota_imposto b
	where	a.nr_sequencia = b.nr_seq_nf_imp_nota
	and		a.nr_seq_nf_interno = nr_sequencia_p  LIMIT 1;

	if (qt_existe_w > 0) then

		select	nextval('nota_fiscal_trib_seq')
		into STRICT 	nr_seq_interno_w
		;

		select	max(imp.vl_imposto),
				max(imp.vl_base_calculo),
				max(imp.tx_imposto),
				max(imp.cd_imposto_interno),--cd_tributo
				max(imp.ie_retencao)
		into STRICT	vl_tributo_w,
				vl_base_calculo_w,
				tx_tributo_w,
				cd_imposto_interno_w,
				ie_retencao_w
		from	nf_imp_nota_imposto imp,
				nf_imp_nota nf
		where	nf.nr_seq_nf_interno = nr_sequencia_p
		and		imp.nr_seq_nf_imp_nota =  nf.nr_sequencia;

		insert into nota_fiscal_trib(
				nr_sequencia,
				cd_tributo,
				vl_tributo,
				dt_atualizacao,
				nm_usuario,
				vl_base_calculo,
				tx_tributo,
				vl_trib_nao_retido,
				vl_base_nao_retido,
				vl_trib_adic,
				vl_base_adic,
				vl_reducao,
				cd_darf,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				cd_variacao,
				nr_seq_interno,
				ie_retencao)
		values (
				nr_sequencia_p,
				coalesce(cd_imposto_interno_w,0),--
				coalesce(vl_tributo_w,0),
				clock_timestamp(),
				wheb_usuario_pck.get_nm_usuario,
				vl_base_calculo_w,
				tx_tributo_w,
				0,
				0,
				0,
				0,
				0,
				0,
				wheb_usuario_pck.get_nm_usuario,
				clock_timestamp(),
				'0',
				nr_seq_interno_w,
				ie_retencao_w );

		commit;
	end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE mx_cfdi_gerar_nf_imp (nr_sequencia_p bigint) FROM PUBLIC;

