SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE neo_atualiza_acao_judicial ( nr_sequencia_p bigint, ie_opcao_p text) AS $body$
DECLARE


codigo_motivo_w		varchar(255);
descricao_motivo_w	varchar(255);
qt_motivo_acao_w	bigint;
matricula_w		varchar(255);
prontuario_w		varchar(255);
data_inicio_w		varchar(255);
data_fim_w		varchar(255);
nr_sequencia_w		varchar(255);
cd_pessoa_fisica_w	varchar(10);
nr_seq_motico_acao_w	bigint;

C01 CURSOR FOR
	SELECT	codigo_motivo,
		descricao_motivo
	from 	w_neo_motivo_acao_jud;


BEGIN

if (ie_opcao_p = '1') then	-- Atualização de motivos de ações judiciais
	open C01;
	loop
	fetch C01 into
		codigo_motivo_w,
		descricao_motivo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select 	count(*)
		into STRICT	qt_motivo_acao_w
		from 	motivo_acao_judicial
		where 	coalesce(cd_motivo_integr,'0') = substr(codigo_motivo_w,1,100);

		if (qt_motivo_acao_w = 0) then

			insert	into MOTIVO_ACAO_JUDICIAL(nr_sequencia,
					ds_motivo,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ie_situacao,
					cd_motivo_integr)
				values (nextval('motivo_acao_judicial_seq'),
					descricao_motivo_w,
					clock_timestamp(),
					'NEO',
					clock_timestamp(),
					'NEO',
					'A',
					substr(codigo_motivo_w,1,100));

		end if;

		end;
	end loop;
	close C01;

elsif (ie_opcao_p = '2') then

	select 	matricula,
		prontuario,
		codigo_motivo,
		data_inicio,
		data_fim
	into STRICT	matricula_w,
		prontuario_w,
		codigo_motivo_w,
		data_inicio_w,
		data_fim_w
	from 	w_neo_acao_judicial
	where	nr_sequencia = nr_sequencia_p;

	select	coalesce(max(cd_pessoa_fisica),'0')
	into STRICT	cd_pessoa_fisica_w
	from	pessoa_fisica
	where	nr_prontuario = prontuario_w;

	if (cd_pessoa_fisica_w <> '0') then

		select 	max(nr_sequencia)
		into STRICT	nr_seq_motico_acao_w
		from 	motivo_acao_judicial
		where	coalesce(cd_motivo_integr,'0') = substr(codigo_motivo_w,1,100);

		if (nr_seq_motico_acao_w IS NOT NULL AND nr_seq_motico_acao_w::text <> '') then

			insert into pessoa_fisica_acao_judic(nr_sequencia,
				cd_pessoa_fisica,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_motivo_acao,
				dt_inicio_acao,
				dt_fim_acao)
			values (nextval('pessoa_fisica_acao_judic_seq'),
				cd_pessoa_fisica_w,
				clock_timestamp(),
				'NEO',
				clock_timestamp(),
				'NEO',
				nr_seq_motico_acao_w,
				to_date(data_inicio_w,'dd/mm/yyyy'),
				to_date(data_fim_w,'dd/mm/yyyy'));

		end if;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
