CREATE OR REPLACE PROCEDURE nfe_atualizar_dados_canc (cd_status_p text, nr_protocolo_p text, nr_danfe_p text, dt_emissao_nfe_p text, ds_versao_aplic_p text, nr_seq_nota_fiscal_p text) AS $body$
DECLARE


dt_emissao_nfe_w	timestamp;


BEGIN

if (dt_emissao_nfe_p IS NOT NULL AND dt_emissao_nfe_p::text <> '') then
	begin
	dt_emissao_nfe_w := replace(dt_emissao_nfe_p, 'T', ' ');

	exception when others then
		begin
		dt_emissao_nfe_w		:= to_date(replace(dt_emissao_nfe_p, 'T', ' '), 'yyyy-mm-dd hh24:mi:ss');
		exception when others then
			begin
			dt_emissao_nfe_w	:= to_date(replace(dt_emissao_nfe_p, 'T', ' '), 'dd-mm-yyyy hh24:mi:ss');
			exception when others then
			--Não foi possível converter a data de emissão enviada no arquivo:
				CALL Wheb_mensagem_pck.exibir_mensagem_abort(186114,'DT_EMISSAO_NFE=' || dt_emissao_nfe_p);
			end;
		end;


	end;
end if;


update	nota_fiscal
	set	ie_status_nfe = cd_status_p,
		nr_nfe_imp_protocolo = nr_protocolo_p,
		nr_danfe = nr_danfe_p,
		dt_autor_prot_nfe =  dt_emissao_nfe_w,
		ds_versao_aplic_nfe = ds_versao_aplic_p
where	nr_sequencia = nr_seq_nota_fiscal_p;

commit;

end;
$body$
