SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE nfe_atualizar_dados_env (cd_status_p text, nr_protocolo_p text, nr_danfe_p text, dt_emissao_nfe_p text, ds_versao_aplic_p text, ds_digest_value_p text, nr_seq_nota_fiscal_p text) AS $body$
DECLARE


dt_emissao_nfe_w	      timestamp;
dt_emissao_aux_w	      varchar(255);
nm_usuario_w		        varchar(255);
reg_integracao_p		    gerar_int_padrao.reg_integracao;
nr_nfe_imp_protocolo_w  nota_fiscal.nr_nfe_imp_protocolo%type;
			

BEGIN

if (dt_emissao_nfe_p IS NOT NULL AND dt_emissao_nfe_p::text <> '') then
	begin
	
	dt_emissao_nfe_w := replace(dt_emissao_nfe_p, 'T', ' ');
	
	exception when others then
		begin               
		dt_emissao_nfe_w		:= to_date(replace(dt_emissao_nfe_p, 'T', ' '), 'yyyy-mm-dd hh24:mi:ss');
		exception when others then
			begin
			dt_emissao_nfe_w	:= to_date(replace(dt_emissao_nfe_p, 'T', ' '), 'dd-mm-yyyy hh24:mi:ss');
			exception when others then
				begin
				
				if (length(dt_emissao_nfe_p) > 19) then
					dt_emissao_aux_w := substr(dt_emissao_nfe_p,1,19);
				else
					dt_emissao_aux_w := dt_emissao_nfe_p;
				end if;
				
				dt_emissao_nfe_w		:= to_date(replace(dt_emissao_aux_w, 'T', ' '), 'yyyy-mm-dd hh24:mi:ss');
				
				exception when others then
  				CALL Wheb_mensagem_pck.exibir_mensagem_abort(186114,'DT_EMISSAO_NFE=' || dt_emissao_nfe_p);
				end;
			end;
		end;


	end;
end if;

select  max(nr_nfe_imp_protocolo)
into STRICT    nr_nfe_imp_protocolo_w
from    nota_fiscal
where   nr_sequencia = nr_seq_nota_fiscal_p;

if (coalesce(nr_nfe_imp_protocolo_w::text, '') = '') then
  update nota_fiscal set nr_nfe_imp_protocolo = nr_protocolo_p where nr_sequencia = nr_seq_nota_fiscal_p;
  commit;
end if;

update	nota_fiscal
	set	ie_status_nfe = cd_status_p,
		nr_danfe = nr_danfe_p,
		dt_autor_prot_nfe =  dt_emissao_nfe_w,
		ds_versao_aplic_nfe = ds_versao_aplic_p,
		ds_digest_value_nfe = ds_digest_value_p
where	nr_sequencia = nr_seq_nota_fiscal_p;			

commit;

select nm_usuario
into STRICT   nm_usuario_w
from   nota_fiscal
where  nr_sequencia = nr_seq_nota_fiscal_p;

reg_integracao_p.ie_operacao		:=	'U';
reg_integracao_p := gerar_int_padrao.gravar_integracao('65', nr_seq_nota_fiscal_p, nm_usuario_w, reg_integracao_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE nfe_atualizar_dados_env (cd_status_p text, nr_protocolo_p text, nr_danfe_p text, dt_emissao_nfe_p text, ds_versao_aplic_p text, ds_digest_value_p text, nr_seq_nota_fiscal_p text) FROM PUBLIC;

