SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE nfse_atualiza_ginfes ( nr_nfe_imp_p text, cd_verificacao_nfse_p text, dt_emissao_nfe_p text, cd_serie_nf_p text, nr_nota_fiscal_p text, dt_emissao_rps_p text) AS $body$
DECLARE


dt_emissao_nfe_w	timestamp;
ie_situacao_w		varchar(1);
dt_emissao_rps_w	timestamp;


BEGIN

if (dt_emissao_rps_p <> 'X') then
	dt_emissao_rps_w := to_date(dt_emissao_rps_p, 'yyyy-mm-dd');
end if;

select  ie_situacao
into STRICT	ie_situacao_w
from 	nota_fiscal
where 	nr_nota_fiscal  	= nr_nota_fiscal_p
and	cd_serie_nf	= cd_serie_nf_p
and 	ie_tipo_nota in ('SE','SD','SF','ST')
and	((to_char(dt_emissao,'dd/mm/yyyy') 	= to_char(dt_emissao_rps_w,'dd/mm/yyyy')) or (dt_emissao_rps_p = 'X'))
and	ie_situacao <> '2';

if (dt_emissao_nfe_p IS NOT NULL AND dt_emissao_nfe_p::text <> '') then
	begin
	dt_emissao_nfe_w := replace(dt_emissao_nfe_p, 'T', ' ');

	exception when others then
		begin
		dt_emissao_nfe_w		:= to_date(replace(dt_emissao_nfe_p, 'T', ' '), 'yyyy-mm-dd hh24:mi:ss');
		exception when others then
			begin
			dt_emissao_nfe_w	:= to_date(replace(dt_emissao_nfe_p, 'T', ' '), 'dd-mm-yyyy hh24:mi:ss');
			exception when others then
			--Não foi possível converter a data de emissão enviada no arquivo:
				CALL Wheb_mensagem_pck.exibir_mensagem_abort(186114,'DT_EMISSAO_NFE=' || dt_emissao_nfe_p);
			end;
		end;


	end;
end if;


if (ie_situacao_w <> '8') then
	update	nota_fiscal
	set 	nr_nfe_imp		=  nr_nfe_imp_p,
		cd_verificacao_nfse 		= cd_verificacao_nfse_p,
		dt_emissao_nfe			= dt_emissao_nfe_w
	where	nr_nota_fiscal		= nr_nota_fiscal_p
	and	cd_serie_nf		= cd_serie_nf_p
	and 	ie_tipo_nota in ('SE','SD','SF','ST')
	and	((to_char(dt_emissao,'dd/mm/yyyy') 	= to_char(dt_emissao_rps_w,'dd/mm/yyyy')) or (dt_emissao_rps_p = 'X'))
	and	ie_situacao in ('1','3');
else
	update	nota_fiscal
	set 	nr_nfe_imp		= nr_nfe_imp_p,
		cd_verificacao_nfse 	= cd_verificacao_nfse_p,
		dt_emissao_nfe		= dt_emissao_nfe_w,
		dt_atualizacao_estoque	= clock_timestamp(),
		ie_situacao		= '1'
	where	nr_nota_fiscal		= nr_nota_fiscal_p
	and	cd_serie_nf		= cd_serie_nf_p
	and	((to_char(dt_emissao,'dd/mm/yyyy') 	= to_char(dt_emissao_rps_w,'dd/mm/yyyy')) or (dt_emissao_rps_p = 'X'))
	and 	ie_tipo_nota in ('SE','SD','SF','ST');

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
