CREATE OR REPLACE PROCEDURE nicu_insert_patient_siblings (nr_sequencia_p nicu_patient_siblings.nr_sequencia%type, nm_usuario_p nicu_patient_siblings.nm_usuario%type, nr_seq_patient_p nicu_patient_siblings.nr_seq_patient%type, ds_name_p nicu_patient_siblings.ds_name%type, ie_gender_p nicu_patient_siblings.ie_gender%type, dt_birthdate_p nicu_patient_siblings.dt_birthdate%type, mensagem_erro_o INOUT text) AS $body$
DECLARE

  
  nr_sequencia_pat_siblings_w  nicu_patient_siblings.nr_sequencia%type;
  exeption_w                   exception;

BEGIN

  if (coalesce(nr_sequencia_p::text, '') = '') then
    --BUSCA NOVO SEQUENCIAL PARA NICU_PARENT
    select nextval('nicu_patient_siblings_seq') 
      into STRICT nr_sequencia_pat_siblings_w
;
  else
    nr_sequencia_pat_siblings_w := nr_sequencia_p;
  end if;

  begin
    insert into nicu_patient_siblings(nr_sequencia,
         dt_atualizacao,
         nm_usuario,
         dt_atualizacao_nrec,
         nm_usuario_nrec,
         nr_seq_patient,
         ds_name,
         ie_gender,
         dt_birthdate)
    values (nr_sequencia_pat_siblings_w,
         clock_timestamp(),
         nm_usuario_p,
         clock_timestamp(),
         nm_usuario_p,
         nr_seq_patient_p,
         ds_name_p,
         ie_gender_p,
         dt_birthdate_p);
  exception
    when unique_violation then
      begin
        update nicu_patient_siblings
           set dt_atualizacao      = clock_timestamp(),
               nm_usuario          = nm_usuario_p,
               dt_atualizacao_nrec = clock_timestamp(),
               nm_usuario_nrec     = nm_usuario_p,
               nr_seq_patient      = nr_seq_patient_p,
               ds_name             = ds_name_p,
               ie_gender           = ie_gender_p,
               dt_birthdate        = dt_birthdate_p
         where nr_sequencia = nr_sequencia_pat_siblings_w;
      exception 
        when others then
          mensagem_erro_o := expressao_pck.obter_desc_expressao(776218)||' Error: '||sqlerrm;
          raise exeption_w;
      end;
    when others then
      mensagem_erro_o := expressao_pck.obter_desc_expressao(776218)||' Error: '||sqlerrm;
      raise exeption_w;
  end;

  commit;
exception
  when exeption_w then
    rollback;
  when others then
    mensagem_erro_o := expressao_pck.obter_desc_expressao(776218)||' Error: '||sqlerrm;
    rollback;
end;
$body$
