CREATE OR REPLACE PROCEDURE nicu_update_clinicians ( nr_encounter_p bigint, nm_usuario_p text, ds_clinicians_p text) AS $body$
DECLARE

							
vl_count bigint;


BEGIN

	select 	count(*)
	into STRICT	vl_count
	from	NICU_ENCOUNTER_STAFF
	where	nr_seq_encounter = nr_encounter_p
	and		IE_TYPE = 'DOCTOR';
	
	if (vl_count > 0) then
		UPDATE 	NICU_ENCOUNTER_STAFF
		set		dt_atualizacao = clock_timestamp(),
				dt_atualizacao_nrec = clock_timestamp(),
				nm_usuario = nm_usuario_p,
				nm_usuario_nrec = nm_usuario_p,
				nm_employee = ds_clinicians_p
		where 	nr_seq_encounter = nr_encounter_p;
	else
		insert into NICU_ENCOUNTER_STAFF(NR_SEQUENCIA,
		NR_SEQ_ENCOUNTER,
		DT_ATUALIZACAO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO,
		NM_USUARIO_NREC,
		IE_TYPE,
		NM_EMPLOYEE)
		values ( nextval('nicu_encounter_staff_seq'),
		nr_encounter_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		'DOCTOR',
		ds_clinicians_p
		);	
	end if;
	commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
