CREATE OR REPLACE PROCEDURE nicu_update_todays_goal ( nr_encounter_p bigint, nm_usuario_p text, ds_goal_p text) AS $body$
DECLARE

							
vl_count bigint;


BEGIN

	select 	count(*)
	into STRICT	vl_count
	from	NICU_PATIENT_GOAL
	where	nr_seq_encounter = nr_encounter_p;
	
	if (vl_count > 0) then
		UPDATE 	NICU_PATIENT_GOAL
		set		dt_atualizacao = clock_timestamp(),
				dt_atualizacao_nrec = clock_timestamp(),
				nm_usuario = nm_usuario_p,
				ds_goal = ds_goal_p
		where 	nr_seq_encounter = nr_encounter_p;
	else
		insert into NICU_PATIENT_GOAL(NR_SEQUENCIA,
		NR_SEQ_ENCOUNTER,
		DT_GOAL_DATE,
		DT_ATUALIZACAO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO,
		NM_USUARIO_NREC,
		DS_GOAL)
		values ( nextval('nicu_patient_goal_seq'),
		nr_encounter_p,
		clock_timestamp(),
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		ds_goal_p
		);	
	end if;
	commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
