SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE nota_saida_entrada_item (cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nm_usuario_p text) AS $body$
DECLARE


contador_w			bigint := 0;
qt_registros_W			bigint;
ds_arquivo_ww			varchar(3000);
nr_sequencia_w			varchar(10);
nr_seq_aux_w			varchar(10);
ie_indicador_movimento_w	varchar(10);
cd_modelo_documento_w		varchar(10);
cd_serie_nf_w			nota_fiscal.cd_serie_nf%type;
nr_nota_fiscal_w		varchar(255);
dt_emissao_documento_w		varchar(8);
nr_item_nf_w			varchar(10);
cd_item_servico_w		varchar(20);
ds_item_servico_w		varchar(255);
cd_fiscal_operacao_w		varchar(255);
cd_natureza_operacao_w		varchar(10);
ds_classificacao_fiscal_w	varchar(255);
qt_item_nf_w			varchar(18);
cd_unidade_medida_compra_w	varchar(30);
vl_unitario_item_nf_w		varchar(20);
vl_total_item_nf_w		varchar(20);
vl_desconto_w			varchar(20);
cd_tributacao_w			varchar(10);
vl_aliquota_ipi_w		varchar(20);
vl_base_calculo_ipi_w		varchar(20);
vl_ipi_w			varchar(20);
ds_situ_trib_estadual_w		varchar(255);
cd_indicador_trib_icms_w	varchar(20);
vl_aliquota_icms_w		varchar(20);
vl_base_calc_icms_w		varchar(20);
vl_icms_w			varchar(20);
vl_base_calc_icmsst_w		varchar(20);
vl_icmsst_w			varchar(20);
cd_mov_fisica_w			varchar(10);
nr_seq_blu_w			varchar(10);

c01 CURSOR FOR
SELECT	n.nr_sequencia														nr_sequencia,
	CASE WHEN n.ie_tipo_nota='EF' THEN 'E' WHEN n.ie_tipo_nota='EN' THEN 'E' WHEN n.ie_tipo_nota='EP' THEN 'E' WHEN n.ie_tipo_nota='SD' THEN 'S' WHEN n.ie_tipo_nota='SE' THEN 'S' WHEN n.ie_tipo_nota='SF' THEN 'S' WHEN n.ie_tipo_nota='ST' THEN 'S' END 					ie_indicador_movimento,
	lpad(' ',2,' ')   													cd_modelo_documento,
	rpad(n.cd_serie_nf,5,' ')												nr_serie_nf,
	lpad(n.nr_nota_fiscal,6,'0')												nr_nota_fiscal,
	lpad(substr(to_char(n.dt_emissao,'ddmmyyyy'),1,8),8,'0')								dt_emissao_documento,
	lpad(i.nr_item_nf,3,'0') 												nr_item_nf,
	lpad(coalesce(substr(CASE WHEN coalesce(i.cd_material::text, '') = '' THEN obter_dados_grupo_servico_item(obter_item_servico_proced(obter_procedimento_nfse(n.nr_sequencia,'P'),obter_procedimento_nfse(n.nr_sequencia,'O')),'CD')  ELSE i.cd_material END ,1,20),' '),20,' ') cd_item_servico,
	rpad(coalesce(CASE WHEN coalesce(i.cd_material::text, '') = '' THEN obter_dados_grupo_servico_item(obter_item_servico_proced(obter_procedimento_nfse(n.nr_sequencia,'P'),obter_procedimento_nfse(n.nr_sequencia,'O')),'DS')  ELSE obter_desc_material(i.cd_material) END ,' '),45, ' ') ds_item_servico,
	lpad(coalesce(substr(obter_dados_natureza_operacao(n.cd_natureza_operacao,'CF'),1,10),' '),3,' ')					cd_fiscal_operacao,
	lpad(coalesce(TO_CHAR(n.cd_natureza_operacao),' '),6,' ') 											cd_natureza_operacao,
	lpad(' ',8,' ')														ds_classificacao_fiscal,
	lpad(replace(replace(replace(to_char(i.qt_item_nf,'999,999,999,990.999'),',',''),'.',''),' ',''),17,' ') 		qt_item_nf,
	rpad(coalesce(i.cd_unidade_medida_compra,' '),3,' ') 											cd_unidade_medida_compra,
	lpad(replace(replace(replace(to_char(i.vl_unitario_item_nf,'999,999,999,990.9999'),',',''),'.',''),' ',''),17,' ')	vl_unitario_item_nf,
	lpad(replace(replace(replace(to_char(i.vl_total_item_nf,'999,999,999,990.99'),',',''),'.',''),' ',''),17,' ')		vl_total_item_nf,
	lpad(replace(replace(replace(to_char(i.vl_desconto,'999,999,999,990.99'),',',''),'.',''),' ',''),17,' ')		vl_desconto,
	lpad(' ',1,' ') 														cd_tributacao,
	lpad(replace(replace(replace(to_char(coalesce(nfe_obter_valor_trib_item(n.nr_sequencia, i.nr_item_nf, 'IPI', 'TX'),0),'999,999,999,990.99'),',',''),'.',''),' ',''),5,' ')	vl_aliquota_ipi,
	lpad(replace(replace(replace(to_char(coalesce(nfe_obter_valor_trib_item(n.nr_sequencia, i.nr_item_nf, 'IPI', 'BC'),0),'999,999,999,990.99'),',',''),'.',''),' ',''),17,' ')	vl_base_calculo_ipi,
	lpad(replace(replace(replace(to_char(coalesce(nfe_obter_valor_trib_item(n.nr_sequencia, i.nr_item_nf, 'IPI', 'TRIB'),0),'999,999,999,990.99'),',',''),'.',''),' ',''),17,' ')	vl_ipi,
	lpad(' ',3,' ') 													ds_situ_trib_estadual,
	lpad(' ',1,' ') 													cd_indicador_trib_icms,
	lpad(replace(replace(replace(to_char(coalesce(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'X', 'ICMS'),0),'999,999,999,990.99'),',',''),'.',''),' ',''),5,' ')vl_aliquota_icms,
	lpad(replace(replace(replace(to_char(coalesce(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ICMS'),0),'999,999,999,990.99'),',',''),'.',''),' ',''),17,' ')vl_base_calc_icms,
	lpad(replace(replace(replace(to_char(coalesce(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'V', 'ICMS'),0),'999,999,999,990.99'),',',''),'.',''),' ',''),17,' ')vl_icms,
	lpad(replace(replace(replace(to_char(coalesce(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ICMSST'),0),'999,999,999,990.99'),',',''),'.',''),' ',''),17,' ')vl_base_calc_icmsst,
	lpad(replace(replace(replace(to_char(coalesce(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'V', 'ICMSST'),0),'999,999,999,990.99'),',',''),'.',''),' ',''),17,' ')vl_icmsst,
	lpad(' ',1,' ') 													cd_mov_fisica
from     nota_fiscal n,
	nota_fiscal_item i,
	operacao_nota o
where    i.nr_sequencia = n.nr_sequencia
and	o.cd_operacao_nf = n.cd_operacao_nf
and	o.ie_servico='N'
and	n.ie_tipo_nota in ('EP','SD','SE','SF','ST')
and	n.dt_emissao between to_date(to_char(dt_inicio_p,'dd/mm/yyyy'),'dd/mm/yyyy') and fim_dia(to_date(to_char(dt_fim_p,'dd/mm/yyyy'),'dd/mm/yyyy'))
order by n.nr_sequencia, i.nr_item_nf;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	ie_indicador_movimento_w,
	cd_modelo_documento_w,
	cd_serie_nf_w,
	nr_nota_fiscal_w,
	dt_emissao_documento_w,
	nr_item_nf_w,
	cd_item_servico_w,
	ds_item_servico_w,
	cd_fiscal_operacao_w,
	cd_natureza_operacao_w,
	ds_classificacao_fiscal_w,
	qt_item_nf_w,
	cd_unidade_medida_compra_w,
	vl_unitario_item_nf_w,
	vl_total_item_nf_w,
	vl_desconto_w,
	cd_tributacao_w,
	vl_aliquota_ipi_w,
	vl_base_calculo_ipi_w,
	vl_ipi_w,
	ds_situ_trib_estadual_w,
	cd_indicador_trib_icms_w,
	vl_aliquota_icms_w,
	vl_base_calc_icms_w,
	vl_icms_w,
	vl_base_calc_icmsst_w,
	vl_icmsst_w,
	cd_mov_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_arquivo_ww	:=	ie_indicador_movimento_w	||
				cd_modelo_documento_w		|| cd_serie_nf_w			||
				nr_nota_fiscal_w		|| dt_emissao_documento_w		||
				nr_item_nf_w			|| cd_item_servico_w			||
				ds_item_servico_w		|| cd_fiscal_operacao_w			||
				cd_natureza_operacao_w		|| ds_classificacao_fiscal_w		||
				qt_item_nf_w			|| cd_unidade_medida_compra_w		||
				vl_unitario_item_nf_w		|| vl_total_item_nf_w			||
				vl_desconto_w			|| cd_tributacao_w			||
				vl_aliquota_ipi_w		|| vl_base_calculo_ipi_w		||
				vl_ipi_w			|| ds_situ_trib_estadual_w		||
				cd_indicador_trib_icms_w	|| vl_aliquota_icms_w			||
				vl_base_calc_icms_w		|| vl_icms_w				||
				vl_base_calc_icmsst_w		|| vl_icmsst_w				||
				cd_mov_fisica_w;

insert into w_inss_direp_arquivo( nr_sequencia,
				cd_estabelecimento,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ds_arquivo_w)
		values (nextval('w_inss_direp_arquivo_seq'),
				cd_estabelecimento_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_arquivo_ww);


	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE nota_saida_entrada_item (cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nm_usuario_p text) FROM PUBLIC;

