CREATE OR REPLACE PROCEDURE nut_alterar_horario_lactario ( dt_horario_p timestamp, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_horario_ant_w	timestamp;
nr_seq_prod_lac_w	bigint;


BEGIN


if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

	select	dt_horario,
		nr_seq_prod_lac
	into STRICT	dt_horario_ant_w,
		nr_seq_prod_lac_w
	from	nut_producao_lactario_item
	where	nr_sequencia = nr_seq_item_p;



	if (coalesce(dt_horario_ant_w::text, '') = '') then
	begin
		select	dt_producao
		into STRICT	dt_horario_ant_w
		from	nut_producao_lactario
		where	nr_sequencia = nr_seq_prod_lac_w;
	end;
	end if;

	update	nut_producao_lactario_item
	set	dt_horario = to_date(to_char(dt_horario_ant_w,'dd/mm/yyyy ')||to_char(dt_horario_p,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_item_p;

	insert into nut_prod_alt_item(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					dt_alteracao,
					dt_horario_ant,
					dt_horario_atual,
					nr_seq_nut_prod_lac_item)
				values (	nextval('nut_prod_alt_item_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					dt_horario_ant_w,
					to_date(to_char(dt_horario_ant_w,'dd/mm/yyyy ')||to_char(dt_horario_p,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'),
					nr_seq_item_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
