CREATE OR REPLACE PROCEDURE nut_assinar_avaliacao ( nr_seq_avaliacao_p bigint, nr_seq_assinatura_p bigint, ie_procedimento_p text, nm_usuario_p text) AS $body$
DECLARE

 
/* 
*** ie_procedimento_p *** 
'A' = Assinar registro digitalmente 
'I' = Inativar avaliação 
*/
 

BEGIN 
 
if (nr_seq_avaliacao_p IS NOT NULL AND nr_seq_avaliacao_p::text <> '') and (nr_seq_assinatura_p IS NOT NULL AND nr_seq_assinatura_p::text <> '') and (nr_seq_assinatura_p > 0) and (ie_procedimento_p IS NOT NULL AND ie_procedimento_p::text <> '')	then 
 
	update 	med_avaliacao_paciente 
	set 	nr_seq_assinatura		= CASE WHEN ie_procedimento_p='A' THEN  nr_seq_assinatura_p  ELSE nr_seq_assinatura END , 
		nr_seq_assinat_inativacao 	= CASE WHEN ie_procedimento_p='I' THEN  nr_seq_assinatura_p  ELSE nr_seq_assinat_inativacao END  
	where 	nr_sequencia = nr_seq_avaliacao_p;
 
	commit;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
