CREATE OR REPLACE PROCEDURE nut_atualizar_obs_posterior ( nr_seq_serv_dia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w	bigint;
nr_seq_servico_w	bigint;
nr_sequencia_w		bigint;
dt_servico_w		timestamp;
ds_observacao_w		varchar(4000);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	nut_atend_serv_dia
	where	nr_atendimento = nr_atendimento_w
	and	nr_seq_servico = nr_seq_servico_w
	and	dt_servico > dt_servico_w
	and	coalesce(dt_liberacao::text, '') = ''
	and	(ds_observacao IS NOT NULL AND ds_observacao::text <> '');


BEGIN
if (nr_seq_serv_dia_p IS NOT NULL AND nr_seq_serv_dia_p::text <> '') then

	select	nr_atendimento,
		nr_seq_servico,
		dt_servico,
		ds_observacao
	into STRICT	nr_atendimento_w,
		nr_seq_servico_w,
		dt_servico_w,
		ds_observacao_w
	from	nut_atend_serv_dia
	where	nr_sequencia = nr_seq_serv_dia_p;

	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
		open C01;
		loop
		fetch C01 into
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			update	nut_atend_serv_dia
			set	ds_observacao = ds_observacao_w
			where	nr_sequencia = nr_sequencia_w;

			end;
		end loop;
		close C01;
	end if;
end if;

commit;

end;
$body$
