CREATE OR REPLACE PROCEDURE nut_atualiza_emissao_acomp ( dt_servico_p timestamp) AS $body$
DECLARE


nr_seq_acomp_w		bigint;

c01 CURSOR FOR
	SELECT	b.nr_sequencia
	from  	NUT_ATEND_ACOMPANHANTE b,
		nut_atend_serv_dia a
	where	a.nr_sequencia = b.nr_seq_atend_serv_dia
	and	trunc(dt_servico_p) = trunc(a.dt_servico)
	and	coalesce(b.dt_impressao_nutricao::text, '') = ''
	group by b.nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into nr_seq_acomp_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		update	NUT_ATEND_ACOMPANHANTE
		set	dt_impressao_nutricao = dt_servico_p
		where	nr_sequencia = nr_seq_acomp_w;

		end;
end loop;
close c01;


commit;

end;
$body$
