SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE nut_calc_custo_real_cardapios (nr_sequencia_p bigint) AS $body$
DECLARE


/* AO ALTERAR ESTA PROCEDURE, DEVE SER VERIFICADO SE A PROCEDURE Calcular_Custo_Real_Comp_Rec PRECISA SER ALTERADA TAMBÃ‰M */

nr_sequencia_comp_w		bigint;
qt_componente_w			double precision;
qt_conversao_w			double precision;
vl_custo_real_w			double precision;
vl_custo_total_w		double precision;
cd_material_w			integer;
qt_pessoa_atend_real_w		bigint;
qt_refeicao_w			bigint;
cd_estabelecimento_w		integer;
nr_seq_gen_alim_w		bigint;
qt_dias_ult_compra_w		bigint;
ie_considerar_pessoas_w		varchar(1);
ie_considerar_comp_w		varchar(1);

C01 CURSOR FOR
	SELECT	n.nr_sequencia,
	coalesce(nr_seq_gen_alim_sub,n.nr_seq_gen_alim),
	CASE WHEN coalesce(nr_seq_gen_alim_sub::text, '') = '' THEN coalesce(n.qt_componente_real,CASE WHEN ie_considerar_comp_w='S' THEN  n.qt_componente  ELSE coalesce(j.qt_componente_calc,j.qt_componente) END )  ELSE coalesce(n.qt_componente_real,n.qt_componente) * coalesce(i.qt_refeicao,1) / coalesce(h.qt_refeicao,1) END ,
	coalesce(i.qt_refeicao,1),
	qt_pessoa_atend_real
from	nut_genero_alim g,
	nut_rec_real_comp n,
	nut_receita_real h,
	nut_receita i,
	nut_receita_comp j,
	nut_cardapio d,
    	nut_cardapio_dia e
where	n.nr_seq_gen_alim	= g.nr_sequencia
and	h.nr_sequencia		= n.nr_seq_rec_real
and	i.nr_sequencia		= h.nr_seq_receita
and	j.nr_seq_receita	= i.nr_sequencia
and	j.nr_seq_gen_alim	= g.nr_sequencia
and	e.nr_sequencia		= d.nr_seq_card_dia
and	d.nr_sequencia		= h.nr_seq_cardapio
and	e.nr_sequencia		= nr_sequencia_p
and	coalesce(ie_comp_adicional,'N') = 'N'

union all

SELECT	n.nr_sequencia,
	coalesce(nr_seq_gen_alim_sub,n.nr_seq_gen_alim),
	coalesce(n.qt_componente_real,n.qt_componente) * coalesce(i.qt_refeicao,1) / coalesce(h.qt_refeicao,1),
	coalesce(i.qt_refeicao,1),
	qt_pessoa_atend_real
from	nut_genero_alim g,
	nut_rec_real_comp n,
	nut_receita_real h,
	nut_receita i,
	nut_cardapio d,
    	nut_cardapio_dia e
where	n.nr_seq_gen_alim	= g.nr_sequencia
and	h.nr_sequencia		= n.nr_seq_rec_real
and	i.nr_sequencia		= h.nr_seq_receita
and	e.nr_sequencia		= d.nr_seq_card_dia
and	d.nr_sequencia		= h.nr_seq_cardapio
and	e.nr_sequencia		= nr_sequencia_p
and	coalesce(ie_comp_adicional,'N') = 'S';


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
	qt_dias_ult_compra_w	:= obter_valor_param_usuario(912, 27, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w);
	ie_considerar_pessoas_w := obter_valor_param_usuario(912,31,obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w);
	ie_considerar_comp_w	:= obter_valor_param_usuario(912,32,obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w);

	open c01;
	loop
	fetch c01 into
		nr_sequencia_comp_w,
		nr_seq_gen_alim_w,
		qt_componente_w,
		qt_refeicao_w,
		qt_pessoa_atend_real_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select 	cd_material,
			coalesce(qt_conversao,1)
		into STRICT	cd_material_w,
			qt_conversao_w
		from	nut_genero_alim
		where 	nr_sequencia = nr_seq_gen_alim_w;

		-- Retirado na OS 642832 - 02/10/2013
		/*select	nvl(max(c.vl_custo),0)
		into	vl_custo_real_w
		from	custo_mensal_material c
		where 	c.cd_estabelecimento	= cd_estabelecimento_w
		and	c.cd_material		= cd_material_w
		and	c.ie_tipo_custo		= 'R'
		and	c.dt_referencia		=	(select	max(dt_referencia)
							from	custo_mensal_material c
							where	c.cd_estabelecimento	= cd_estabelecimento_w
							and	c.cd_material		= cd_material_w
							and	c.ie_tipo_custo		= 'R');*/
		select	coalesce(obter_dados_ultima_compra(cd_estabelecimento_w, cd_material_w, 'VE'),0)
		into STRICT	vl_custo_real_w
		;

		if (coalesce(qt_refeicao_w,0) = 0 ) then
			qt_componente_w  := 0;
		else
			if (ie_considerar_pessoas_w = 'S') then
				qt_componente_w		:= qt_componente_w * qt_pessoa_atend_real_w / qt_refeicao_w;
			else
				qt_componente_w		:= qt_componente_w / qt_refeicao_w;
			end if;
		end if;

		vl_custo_total_w	:= vl_custo_real_w * qt_componente_w / qt_conversao_w;

		update	nut_rec_real_comp
		set	vl_custo_real	=  vl_custo_total_w
		where	nr_sequencia	= nr_sequencia_comp_w;

		end;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE nut_calc_custo_real_cardapios (nr_sequencia_p bigint) FROM PUBLIC;

