CREATE OR REPLACE PROCEDURE nut_cardapio_planejamento ( nr_sequencia_p bigint, nr_seq_cardapio_dia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencias_w		varchar(2000);
nr_sequencia_w		bigint;
nr_pos_virgula_w	bigint;
nr_seq_comp_w         	bigint;
nr_seq_receita_w       	bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	SELECT	max(nr_seq_comp),
		max(nr_seq_receita)
	into STRICT	nr_seq_comp_w,
		nr_seq_receita_w
	FROM   	nut_cardapio
	WHERE	nr_sequencia = nr_sequencia_p;

	insert into nut_pac_opcao_rec(
				nr_sequencia,
				nr_seq_composicao,
				nr_seq_receita,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_tipo_cardapio,
				nr_seq_cardapio_dia,
				nr_seq_cardapio)
		values (		nextval('nut_pac_opcao_rec_seq'),
				nr_seq_comp_w,
				nr_seq_receita_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				'PL',
				nr_seq_cardapio_dia_p,
				nr_sequencia_p);

end if;
commit;

end;
$body$
