CREATE OR REPLACE PROCEDURE nut_cardapio_plan_todos ( nr_sequencias_p text, nr_seq_cardapio_dia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencias_w		varchar(2000);
nr_sequencia_w		bigint;
nr_pos_virgula_w	bigint;


BEGIN
if (nr_sequencias_p IS NOT NULL AND nr_sequencias_p::text <> '') then
	begin

	delete
	FROM nut_pac_opcao_rec
	WHERE nr_seq_cardapio_dia = nr_seq_cardapio_dia_p;

	nr_sequencias_w	:= nr_sequencias_p;
	while(nr_sequencias_w IS NOT NULL AND nr_sequencias_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in nr_sequencias_w);
		if (nr_pos_virgula_w > 0) then
			begin
			nr_sequencia_w	:= (substr(nr_sequencias_w,1,nr_pos_virgula_w-1))::numeric;
			nr_sequencias_w	:= substr(nr_sequencias_w,nr_pos_virgula_w+1,length(nr_sequencias_w));
			if (nr_sequencia_w > 0) then
				begin

				CALL nut_cardapio_planejamento(nr_sequencia_w, nr_seq_cardapio_dia_p, nm_usuario_p);

				end;
			end if;
			end;
		else
			begin
			nr_sequencias_w	:= null;
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
