CREATE OR REPLACE PROCEDURE nut_definir_nivel_assist ( nr_atendimento_p bigint, nr_seq_nivel_assist_p bigint, nr_seq_classificacao_pac_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_dados_pac_w		bigint;

nr_seq_nivel_assist_w		bigint;
nr_seq_classificacao_pac_w	bigint;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_dados_pac_w
	from	nut_dados_paciente
	where	nr_atendimento = nr_atendimento_p;

	if (coalesce(nr_seq_dados_pac_w::text, '') = '') then

		insert	into	nut_dados_paciente(	nr_sequencia,
							dt_atualizacao,
							dt_Atualizacao_nrec,
							nm_usuario,
							nm_usuario_nrec,
							nr_atendimento,
							nr_seq_nivel_assistencial,
							nr_seq_classificacao_pac)
					values (	nextval('nut_dados_paciente_seq'),
							clock_timestamp(),
							clock_timestamp(),
							nm_usuario_p,
							nm_usuario_p,
							nr_atendimento_p,
							nr_seq_nivel_assist_p,
							nr_seq_classificacao_pac_p);
	else
		select	max(nr_seq_nivel_assistencial),
			max(nr_seq_classificacao_pac)
		into STRICT	nr_seq_nivel_assist_w,
			nr_seq_classificacao_pac_w
		from	nut_dados_paciente
		where	nr_sequencia = nr_seq_dados_pac_w;

		update	nut_dados_paciente
		set	nr_seq_nivel_assistencial = coalesce(nr_seq_nivel_assist_p,nr_seq_nivel_assistencial),
			nr_seq_classificacao_pac = coalesce(nr_seq_classificacao_pac_p,nr_seq_classificacao_pac)
		where	nr_sequencia = nr_seq_dados_pac_w;

		if (nr_seq_nivel_assist_p IS NOT NULL AND nr_seq_nivel_assist_p::text <> '') then
			CALL nut_atualiza_dados_pac_hist(nr_seq_nivel_assist_p,nr_seq_nivel_assist_w,null,null,nm_usuario_p);
		else
			CALL nut_atualiza_dados_pac_hist(null,null,nr_seq_classificacao_pac_p,nr_seq_classificacao_pac_w,nm_usuario_p);
		end if;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
