SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE nut_desfazer_conferir_serv ( nr_atendimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


nr_seq_servico_w	bigint;
nr_seq_serv_conf_w	bigint;

/*
ie_opcao_p : 'CF'    = Conferir
	      'DCF' = Desfazer conferencia
*/
C01 CURSOR FOR
	SELECT  distinct x.nr_sequencia
	from	nut_atend_serv_dia x,
		nut_servico a,
		nut_atend_serv_dia_rep y
	where	a.nr_sequencia = x.nr_seq_servico
	and	x.nr_sequencia = y.nr_seq_serv_dia
	and	x.nr_atendimento =  nr_atendimento_p
	and	(x.dt_bloqueio IS NOT NULL AND x.dt_bloqueio::text <> '')
	and	x.dt_servico between dt_inicial_p and dt_final_p
	and	coalesce(x.dt_conferencia::text, '') = ''
	and	coalesce(x.nm_usuario_conf::text, '') = ''
	and	ie_opcao_p = 'CF';

C02 CURSOR FOR
	SELECT  distinct x.nr_sequencia
	from	nut_atend_serv_dia x,
		nut_servico a,
		nut_atend_serv_dia_rep y
	where	a.nr_sequencia = x.nr_seq_servico
	and	x.nr_sequencia = y.nr_seq_serv_dia
	and	x.nr_atendimento =  nr_atendimento_p
	and	(x.dt_bloqueio IS NOT NULL AND x.dt_bloqueio::text <> '')
	and	x.dt_servico between dt_inicial_p and dt_final_p
	and	(x.dt_conferencia IS NOT NULL AND x.dt_conferencia::text <> '')
	and	(x.nm_usuario_conf IS NOT NULL AND x.nm_usuario_conf::text <> '')
	and	ie_opcao_p = 'DCF';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_servico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	nut_atend_serv_dia
	set	dt_conferencia 	= clock_timestamp(),
		nm_usuario_conf	= nm_usuario_p
	where	nr_sequencia 	= nr_seq_servico_w
	and	nr_atendimento 	= nr_atendimento_p;

	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_serv_conf_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	update	nut_atend_serv_dia
	set	dt_conferencia 	 = NULL,
		nm_usuario_conf	 = NULL,
		dt_desfazer_conf 	= clock_timestamp(),
		nm_usu_desf_conf 	= nm_usuario_p
	where	nr_sequencia 	= nr_seq_serv_conf_w
	and	nr_atendimento 	= nr_atendimento_p;
	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
