CREATE OR REPLACE PROCEDURE nut_duplicar_itens_extra ( nr_atendimento_p bigint, dt_servico_base_p timestamp, dt_servico_inicio_p timestamp, dt_servico_final_p timestamp, cd_setor_atendimento_p bigint, nm_usuario_p text, nr_seq_servico_p bigint) AS $body$
DECLARE



nr_seq_servico_w	bigint;
nr_seq_composicao_w	bigint;
nr_seq_receita_w	bigint;
ie_tipo_requisicao_w	varchar(1);
ie_tipo_pessoa_w	varchar(1);
nr_seq_servico_extra_w	bigint;
nr_seq_gen_alim_w	bigint;
nr_seq_servico_novo_w	bigint;
nr_seq_nut_atend_w	bigint;
qt_inserido_w		bigint;


C01 CURSOR FOR
	SELECT	nr_seq_servico,
		nr_sequencia
	from	NUT_ATEND_SERV_DIA
	where	nr_atendimento		= nr_atendimento_p
	and	cd_setor_atendimento	= cd_setor_atendimento_p
	and	dt_servico between  trunc(dt_servico_inicio_p) and trunc(dt_servico_final_p) + 86399/86400
	and	((nr_seq_servico = nr_seq_servico_p) or (coalesce(nr_seq_servico_p::text, '') = ''))
	order by 1;

C02 CURSOR FOR
	SELECT	b.nr_seq_servico,
		b.nr_seq_composicao,
		b.nr_seq_receita,
		b.ie_tipo_requisicao,
		b.ie_tipo_pessoa,
		b.nr_seq_servico_extra,
		b.nr_seq_gen_alim
	from	NUT_ATEND_SERV_DIA a,
		NUT_ITEM_EXTRA b
	where	a.nr_sequencia		= b.nr_seq_servico
	and	a.nr_seq_servico	= nr_seq_servico_novo_w
	and	cd_setor_atendimento	= cd_setor_atendimento_p
	and	a.nr_atendimento	= nr_atendimento_p
	and	a.dt_servico		between  trunc(dt_servico_base_p) and trunc(dt_servico_base_p) + 86399/86400
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_servico_novo_w,
	nr_seq_nut_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_servico_w,
		nr_seq_composicao_w,
		nr_seq_receita_w,
		ie_tipo_requisicao_w,
		ie_tipo_pessoa_w,
		nr_seq_servico_extra_w,
		nr_seq_gen_alim_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		select	count(*)
		into STRICT	qt_inserido_w
		from	NUT_ITEM_EXTRA
		where	nr_seq_servico		= nr_seq_nut_atend_w
		and (coalesce(nr_seq_servico_extra, nr_seq_servico_extra_w)	= nr_seq_servico_extra_w or (coalesce(nr_seq_servico_extra_w::text, '') = ''))
		and (coalesce(nr_Seq_composicao, nr_seq_composicao_w)		= nr_seq_composicao_w or (coalesce(nr_seq_composicao_w::text, '') = ''))
		and (coalesce(nr_seq_receita, nr_seq_receita_w)			= nr_seq_receita_w or (coalesce(nr_seq_receita_w::text, '') = ''))
		and (coalesce(nr_seq_gen_alim, nr_seq_gen_alim_w)		= nr_seq_gen_alim_w or (coalesce(nr_seq_gen_alim_w::text, '') = ''))
		and (coalesce(ie_tipo_pessoa, ie_tipo_pessoa_w)			= ie_tipo_pessoa_w or (coalesce(ie_tipo_pessoa_w::text, '') = ''))
		and (coalesce(ie_tipo_Requisicao, ie_tipo_requisicao_w)		= ie_tipo_requisicao_w or (coalesce(ie_tipo_requisicao_w::text, '') = ''));

		if (qt_inserido_w	= 0) then
			insert into NUT_ITEM_EXTRA(NR_SEQUENCIA,
					DT_ATUALIZACAO,
					NM_USUARIO,
					DT_ATUALIZACAO_NREC,
					NM_USUARIO_NREC,
					NR_SEQ_SERVICO,
					NR_SEQ_COMPOSICAO,
					NR_SEQ_RECEITA,
					IE_TIPO_REQUISICAO,
					DT_LIBERACAO,
					NM_USUARIO_LIBERACAO,
					DT_ENTREGA,
					NM_USUARIO_ENTREGA,
					IE_TIPO_PESSOA,
					NR_SEQ_SERVICO_EXTRA,
					NR_SEQ_GEN_ALIM)
				values (nextval('nut_item_extra_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_nut_atend_w,
					nr_seq_composicao_w,
					nr_seq_receita_w,
					ie_tipo_requisicao_w,
					null,
					null,
					null,
					null,
					ie_tipo_pessoa_w,
					nr_seq_servico_extra_w,
					nr_seq_gen_alim_w);
		end if;

		end;
	end loop;
	close C02;


	end;
end loop;
close C01;

commit;

end;
$body$
