SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE nut_inserir_prescr_dieta (nr_seq_nutricao_p bigint, ds_lista_dieta_p text, nm_usuario_p text) AS $body$
DECLARE


lista_informacao_w		varchar(1000);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
cd_dieta_w			bigint;
nr_sequencia_w			bigint;


BEGIN

lista_informacao_w	:= ds_lista_dieta_p;

while	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or
	ie_contador_w > 200 loop
	begin

	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);

	/* Obter a sequência lida */

	if (ie_pos_virgula_w <> 0) then
		cd_dieta_w		:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	/* Obterm a nova sequência */

	select	nextval('nut_prescr_dieta_seq')
	into STRICT	nr_sequencia_w
	;

	insert into nut_prescr_dieta(
		nr_sequencia,
		nr_nut_prescricao,
		nr_prescricao,
		nr_seq_prescricao,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_dieta_medico,
		cd_dieta_nutricao
	) values (
		nr_sequencia_w,
		nr_seq_nutricao_p,
		null,
		null,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		null,
		cd_dieta_w);

	ie_contador_w	:= ie_contador_w + 1;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
