CREATE OR REPLACE PROCEDURE nut_inserir_servico_acomp ( nr_seq_servico_p bigint, dt_inicio_p timestamp, nr_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_pessoa_fisica_p text, nm_pessoa_fisica_p text, nm_usuario_p text, nr_seq_Acompanhante_p INOUT bigint) AS $body$
DECLARE


nr_Sequencia_w		bigint;
nr_seq_serv_dia_w	bigint;
qt_servico_w		bigint;
dt_servico_w		timestamp;
cd_dieta_w		bigint;
qt_dieta_acomp_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_servico_w
from	nut_atend_serv_dia
where	trunc(dt_servico,'dd') = trunc(dt_inicio_p,'dd')
and	nr_atendimento = nr_atendimento_p
and	nr_seq_servico = nr_seq_servico_p;

select	to_date(to_char(dt_inicio_p,'dd/mm/yyyy')||' '||obter_horario_servico(nr_seq_servico_p, cd_setor_atendimento_p), 'dd/mm/yyyy hh24:mi')
into STRICT	dt_servico_w
;

if (qt_servico_w = 0) then

	select	nextval('nut_atend_serv_dia_seq')
	into STRICT	nr_seq_serv_dia_w
	;

	insert into nut_atend_serv_dia(	nr_sequencia,
					nr_seq_servico,
					nr_Atendimento,
					dt_servico,
					cd_setor_atendimento,
					nm_usuario,
					nm_usuario_nrec,
					dt_atualizacao,
					dt_atualizacao_nrec,
					ie_acompanhante)
			values (nr_seq_serv_dia_w,
					nr_seq_servico_p,
					nr_atendimento_p,
					dt_servico_w,
					cd_setor_atendimento_p,
					nm_usuario_p,
					nm_usuario_p,
					clock_timestamp(),
					clock_timestamp(),
					'S');
else
	select	max(nr_sequencia)
	into STRICT	nr_seq_serv_dia_w
	from	nut_atend_serv_dia
	where	trunc(dt_servico,'dd') = trunc(dt_inicio_p,'dd')
	and	nr_atendimento = nr_atendimento_p
	and	nr_seq_servico = nr_seq_servico_p;

	update	nut_atend_serv_dia
	set	ie_acompanhante = 'S'
	where	nr_sequencia  = nr_seq_serv_dia_w;
end if;

select	nextval('nut_atend_acompanhante_seq')
into STRICT	nr_Sequencia_w
;

insert into nut_atend_acompanhante(	nr_Sequencia,
					cd_pessoa_fisica,
					nm_pessoa_fisica,
					NR_SEQ_ATEND_SERV_DIA,
					dt_atualizacao,
					dt_atualizacao_nrec,
					nm_usuario,
					nm_usuario_nrec)
		values (	nr_sequencia_w,
					cd_pessoa_fisica_p,
					nm_pessoa_fisica_p,
					nr_seq_serv_dia_w,
					clock_timestamp(),
					clock_timestamp(),
					nm_usuario_p,
					nm_usuario_p);


nr_seq_Acompanhante_p := nr_Sequencia_w;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
