CREATE OR REPLACE PROCEDURE nut_liberar_solicitacao_lanche ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_evento_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_seq_evento 
	from 	regra_envio_sms 
	where	cd_estabelecimento = obter_estabelecimento_ativo 
	and	ie_evento_disp 	= 'LIBNUT' 
	and	coalesce(ie_situacao,'A') = 'A';
	

BEGIN 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	update 	nut_solic_lanche 
	set	dt_liberacao = clock_timestamp(), 
		nm_usuario_liberacao = nm_usuario_p 
	where 	nr_sequencia = nr_sequencia_p;
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin				 
		CALL gerar_evento_paciente(nr_seq_evento_w, null, null, null,nm_usuario_p);
		end;
	end loop;
	close C01;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
